-- 42. Black Russian
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c5d6e7f8-9a0b-1c2d-3e4f-5a6b7c8d9e0f', 'black-russian', 'black_russian_iba_contemporary_classic', '{\"en\":\"Black Russian\",\"it\":\"Black Russian\",\"vi\":\"Black Russian\",\"ko\":\"블랙 러시안\",\"ja\":\"ブラックルシアン\"}'::jsonb, 'iba_official', 'Contemporary', '{\"iba\",\"official\",\"simple\",\"coffee\",\"after-dinner\",\"spirit-forward\"}', '{\"en\":\"A simple yet sophisticated cocktail combining vodka with coffee liqueur. Strong, smooth, and dangerously easy-drinking - the Black Russian is the perfect after-dinner indulgence.\",\"it\":\"Un cocktail semplice ma sofisticato che combina vodka con liquore al caffè. Forte, morbido e pericolosamente facile da bere - il Black Russian è la perfetta indulgenza dopo cena.\",\"vi\":\"Một cocktail đơn giản nhưng tinh tế kết hợp vodka với rượu mùi cà phê. Mạnh, mượt mà và nguy hiểm dễ uống - Black Russian là sự nuông chiều hoàn hảo sau bữa tối.\"}'::jsonb, '{\"created_year\":\"1949\",\"origin\":{\"city\":\"Brussels\",\"bar\":\"Hotel Metropole\",\"country\":\"Belgium\"},\"creator\":{\"name\":\"Gustave Tops\",\"profession\":\"bartender\"},\"story\":{\"en\":\"Created by bartender Gustave Tops at the Hotel Metropole in Brussels, Belgium in 1949. It was created for Perle Mesta, the U.S. Ambassador to Luxembourg. The drink gained popularity during the Cold War era, and its name reflects the Russian origins of vodka. The White Russian variation (adding cream) came later.\",\"it\":\"Creato dal barman Gustave Tops all''Hotel Metropole di Bruxelles, Belgio nel 1949. Fu creato per Perle Mesta, l''ambasciatore statunitense in Lussemburgo. La bevanda ottenne popolarità durante l''era della Guerra Fredda, e il suo nome riflette le origini russe della vodka. La variante White Russian (aggiungendo panna) venne dopo.\",\"vi\":\"Được tạo ra bởi bartender Gustave Tops tại khách sạn Metropole ở Brussels, Bỉ vào năm 1949. Nó được tạo ra cho Perle Mesta, Đại sứ Mỹ tại Luxembourg. Thức uống trở nên phổ biến trong thời kỳ Chiến tranh Lạnh, và tên của nó phản ánh nguồn gốc Nga của vodka. Biến thể White Russian (thêm kem) ra đời sau đó.\"},\"named_after\":{\"en\":\"Named \"Black Russian\" for the Russian origins of vodka and the dark color from coffee liqueur.\",\"it\":\"Chiamato \"Black Russian\" per le origini russe della vodka e il colore scuro del liquore al caffè.\",\"vi\":\"Được đặt tên \"Black Russian\" cho nguồn gốc Nga của vodka và màu đen từ rượu mùi cà phê.\"}}'::jsonb, '{\"profile\":[\"coffee\",\"sweet\",\"boozy\"],\"description\":{\"en\":\"Rich coffee flavor with smooth vodka backbone. Kahlúa provides sweet coffee notes while vodka adds clean strength. Smooth, slightly sweet, and deceptively strong.\",\"it\":\"Ricco sapore di caffè con morbida base di vodka. Il Kahlúa fornisce note dolci di caffè mentre la vodka aggiunge forza pulita. Morbido, leggermente dolce e inganevolmente forte.\",\"vi\":\"Hương vị cà phê đậm đà với xương sống vodka mượt mà. Kahlúa cung cấp hương cà phê ngọt trong khi vodka thêm sức mạnh sạch. Mượt mà, hơi ngọt và mạnh đáng lừa.\"},\"first_impression\":{\"en\":\"Sweet coffee with smooth vodka strength\",\"it\":\"Caffè dolce con forza morbida di vodka\",\"vi\":\"Cà phê ngọt với sức mạnh vodka mượt\"},\"finish\":{\"en\":\"Long, warming finish with lingering coffee notes\",\"it\":\"Finale lungo e caldo con note di caffè persistenti\",\"vi\":\"Kết thúc dài, ấm áp với hương cà phê kéo dài\"},\"balance\":{\"en\":\"Well-balanced between sweet coffee liqueur and clean vodka\",\"it\":\"Ben bilanciato tra liquore al caffè dolce e vodka pulita\",\"vi\":\"Cân bằng tốt giữa rượu mùi cà phê ngọt và vodka sạch\"}}'::jsonb, '{\"best_time\":[\"evening\",\"late_night\"],\"occasions\":[\"after-dinner\",\"nightcap\",\"digestivo\",\"casual\"],\"seasons\":[\"autumn\",\"winter\",\"spring\",\"summer\"],\"food_pairings\":{\"en\":\"Perfect after dinner or with desserts, especially chocolate, tiramisu, or coffee-flavored sweets.\",\"it\":\"Perfetto dopo cena o con dessert, specialmente cioccolato, tiramisù o dolci al caffè.\",\"vi\":\"Hoàn hảo sau bữa tối hoặc với món tráng miệng, đặc biệt là chocolate, tiramisu hoặc đồ ngọt vị cà phê.\"},\"ideal_for\":{\"en\":\"Perfect for coffee and vodka lovers seeking a simple, strong cocktail. Ideal for those who prefer spirit-forward drinks.\",\"it\":\"Perfetto per gli amanti del caffè e della vodka che cercano un cocktail semplice e forte. Ideale per chi preferisce drink spirit-forward.\",\"vi\":\"Hoàn hảo cho người yêu cà phê và vodka tìm kiếm cocktail đơn giản, mạnh. Lý tưởng cho những ai thích đồ uống hướng về rượu.\"}}'::jsonb, '[{\"ingredient_id\":\"ING_VODKA\",\"quantity\":{\"amount\":50,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Vodka\",\"it\":\"Vodka\",\"vi\":\"Vodka\"}},{\"ingredient_id\":\"ING_COFFEE_LIQUEUR\",\"quantity\":{\"amount\":20,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Coffee liqueur (Kahlúa)\",\"it\":\"Liquore al caffè (Kahlúa)\",\"vi\":\"Rượu mùi cà phê (Kahlúa)\"}]'::jsonb, 'build', '{\"en\":\"Pour ingredients into an old fashioned glass filled with ice cubes. Stir gently. No garnish required.\",\"it\":\"Versare gli ingredienti in un bicchiere old fashioned pieno di cubetti di ghiaccio. Mescolare delicatamente. Nessuna guarnizione richiesta.\",\"vi\":\"Đổ nguyên liệu vào ly old fashioned đầy đá viên. Khuấy nhẹ. Không cần trang trí.\"}'::jsonb, 'Old Fashioned glass', '{\"en\":\"None\",\"it\":\"Nessuna\",\"vi\":\"Không\"}'::jsonb, 'cubed', 'on_the_rocks', '{\"ING_VODKA\"}', '{\"coffee\",\"sweet\",\"boozy\"}', 28, 220, 'easy', 30, '{\"sulphites\"}', '{\"alcohol\",\"sulphites_intolerance\",\"caffeine\"}', '{\"vegan\",\"vegetarian\",\"pescatarian\",\"gluten_free\",\"dairy_free\",\"nut_free\"}', 0, '{\"vegan\",\"gluten-free\",\"dairy-free\"}', '{\"autumn\",\"winter\",\"spring\",\"summer\"}', '{\"after-dinner\",\"nightcap\",\"digestivo\",\"casual\"}', false, false, '{\"white-russian\",\"dirty-russian\",\"blind-russian\"}', 'Simple two-ingredient build. The White Russian adds cream on top. Kahlúa is the traditional coffee liqueur, but any good coffee liqueur works. Can be served neat or on the rocks. Deceptively strong - warn guests.', 'low', 70, 'https://iba-world.com/black-russian/', 'IBA Official Recipe.', 1)
