-- 58. Kir
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d0e1f2a3-4b5c-6d7e-8f9a-0b1c2d3e4f5a', 'kir', 'kir_iba_contemporary_classic', '{\"en\":\"Kir\",\"it\":\"Kir\",\"vi\":\"Kir\",\"ko\":\"키르\",\"ja\":\"キール\"}'::jsonb, 'iba_official', 'Contemporary', '{\"iba\",\"official\",\"french\",\"aperitif\",\"wine-based\",\"elegant\",\"simple\"}', '{\"en\":\"A classic French apéritif combining dry white wine with a touch of crème de cassis. Elegant, simple, and quintessentially French - the perfect way to begin an evening.\",\"it\":\"Un classico aperitivo francese che combina vino bianco secco con un tocco di crème de cassis. Elegante, semplice e quintessenzialmente francese - il modo perfetto per iniziare una serata.\",\"vi\":\"Một aperitif Pháp cổ điển kết hợp rượu vang trắng khô với một chút crème de cassis. Thanh lịch, đơn giản và rất Pháp - cách hoàn hảo để bắt đầu buổi tối.\"}'::jsonb, '{\"created_year\":\"1945\",\"origin\":{\"city\":\"Dijon\",\"bar\":\"Unknown\",\"country\":\"France\"},\"creator\":{\"name\":\"Félix Kir\",\"profession\":\"mayor\"},\"story\":{\"en\":\"Named after Félix Kir, the mayor of Dijon, France, who popularized the drink after World War II. Originally called \"blanc-cassis\" (white-cassis), it was renamed in his honor. Kir served it to visiting dignitaries to promote local Burgundian products - Aligoté wine and crème de cassis from blackcurrants.\",\"it\":\"Prende il nome da Félix Kir, il sindaco di Digione, Francia, che rese popolare la bevanda dopo la Seconda Guerra Mondiale. Originariamente chiamato \"blanc-cassis\" (bianco-cassis), fu rinominato in suo onore. Kir lo serviva ai dignitari in visita per promuovere i prodotti locali della Borgogna - vino Aligoté e crème de cassis da ribes nero.\",\"vi\":\"Được đặt theo tên Félix Kir, thị trưởng Dijon, Pháp, người đã phổ biến thức uống sau Thế chiến II. Ban đầu được gọi là \"blanc-cassis\" (trắng-cassis), nó được đổi tên để vinh danh ông. Kir phục vụ nó cho các chính khách đến thăm để quảng bá các sản phẩm địa phương Burgundy - rượu vang Aligoté và crème de cassis từ quả lý chua đen.\"},\"named_after\":{\"en\":\"Named after Félix Kir, the mayor of Dijon who popularized the drink.\",\"it\":\"Prende il nome da Félix Kir, il sindaco di Digione che rese popolare la bevanda.\",\"vi\":\"Được đặt theo tên Félix Kir, thị trưởng Dijon người đã phổ biến thức uống.\"}}'::jsonb, '{\"profile\":[\"fruity\",\"dry\",\"light\"],\"description\":{\"en\":\"Light, crisp, and refreshing with subtle blackcurrant sweetness complementing the dry white wine. Elegant and easy-drinking with beautiful berry aromatics.\",\"it\":\"Leggero, croccante e rinfrescante con sottile dolcezza di ribes nero che complementa il vino bianco secco. Elegante e facile da bere con bellissimi aromi di bacche.\",\"vi\":\"Nhẹ nhàng, giòn và sảng khoái với vị ngọt lý chua đen tinh tế bổ sung cho rượu vang trắng khô. Thanh lịch và dễ uống với hương quả mọng đẹp.\"},\"first_impression\":{\"en\":\"Bright blackcurrant with crisp white wine\",\"it\":\"Ribes nero brillante con vino bianco croccante\",\"vi\":\"Lý chua đen sáng với rượu vang trắng giòn\"},\"finish\":{\"en\":\"Clean, dry finish with lingering berry notes\",\"it\":\"Finale pulito e secco con note di bacche persistenti\",\"vi\":\"Kết thúc sạch, khô với hương quả mọng kéo dài\"},\"balance\":{\"en\":\"Well-balanced between dry wine and sweet cassis\",\"it\":\"Ben bilanciato tra vino secco e cassis dolce\",\"vi\":\"Cân bằng tốt giữa rượu vang khô và cassis ngọt\"}}'::jsonb, '{\"best_time\":[\"afternoon\",\"evening\"],\"occasions\":[\"aperitif\",\"casual\",\"outdoor\",\"french_dining\"],\"seasons\":[\"spring\",\"summer\",\"autumn\"],\"food_pairings\":{\"en\":\"Perfect as an apéritif before dinner. Pairs well with French cheeses, charcuterie, olives, and light appetizers.\",\"it\":\"Perfetto come aperitivo prima di cena. Si abbina bene con formaggi francesi, salumi, olive e antipasti leggeri.\",\"vi\":\"Hoàn hảo như một aperitif trước bữa tối. Kết hợp tốt với phô mai Pháp, thịt nguội, ô liu và món khai vị nhẹ.\"},\"ideal_for\":{\"en\":\"Ideal for wine lovers seeking a lighter apéritif. Perfect for those who appreciate French elegance and simplicity.\",\"it\":\"Ideale per gli amanti del vino che cercano un aperitivo più leggero. Perfetto per chi apprezza l''eleganza e la semplicità francese.\",\"vi\":\"Lý tưởng cho người yêu rượu vang tìm kiếm aperitif nhẹ hơn. Hoàn hảo cho những ai đánh giá cao sự thanh lịch và đơn giản của Pháp.\"}}'::jsonb, '[{\"ingredient_id\":\"ING_DRY_WHITE_WINE\",\"quantity\":{\"amount\":90,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Dry white wine (preferably Aligoté)\",\"it\":\"Vino bianco secco (preferibilmente Aligoté)\",\"vi\":\"Rượu vang trắng khô (tốt nhất là Aligoté)\"}},{\"ingredient_id\":\"ING_CREME_DE_CASSIS\",\"quantity\":{\"amount\":10,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Crème de Cassis\",\"it\":\"Crème de Cassis\",\"vi\":\"Crème de Cassis\"}]'::jsonb, 'build', '{\"en\":\"Pour crème de cassis into a wine glass. Top with chilled white wine. Stir gently. No garnish required.\",\"it\":\"Versare la crème de cassis in un bicchiere da vino. Completare con vino bianco freddo. Mescolare delicatamente. Nessuna guarnizione richiesta.\",\"vi\":\"Đổ crème de cassis vào ly rượu vang. Rót rượu vang trắng lạnh lên trên. Khuấy nhẹ. Không cần trang trí.\"}'::jsonb, 'White wine glass', '{\"en\":\"None\",\"it\":\"Nessuna\",\"vi\":\"Không\"}'::jsonb, 'none', 'up', '{\"ING_DRY_WHITE_WINE\"}', '{\"fruity\",\"dry\",\"light\"}', 10, 120, 'easy', 30, '{\"sulphites\"}', '{\"alcohol\",\"sulphites_intolerance\"}', '{\"vegan\",\"vegetarian\",\"pescatarian\",\"gluten_free\",\"dairy_free\",\"nut_free\"}', 0, '{\"vegan\",\"gluten-free\",\"dairy-free\"}', '{\"spring\",\"summer\",\"autumn\"}', '{\"aperitif\",\"casual\",\"outdoor\",\"french_dining\"}', false, false, '{\"kir-royale\",\"kir-imperial\",\"kir-breton\"}', 'Traditionally made with Burgundian Aligoté wine, but any dry white wine works. The Kir Royale variation uses champagne instead of white wine. Ratio can be adjusted - typically 1:9 cassis to wine. Serve well chilled.', 'low', 60, 'https://iba-world.com/kir/', 'IBA Official Recipe. Traditional French apéritif.', 1)
