-- 100. Russian Spring Punch
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('1e2f3a4b-5c6d-7e8f-9a0b-1c2d3e4f5a6b', 'russian-spring-punch', 'e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0', '{\"en\":\"Russian Spring Punch\",\"it\":\"Russian Spring Punch\",\"vi\":\"Russian Spring Punch\",\"ko\":\"러시안 스프링 펀치\",\"ja\":\"ロシアンスプリングパンチ\"}'::jsonb, 'iba_official', 'NewEraDrinks', '{\"iba\",\"official\",\"vodka\",\"champagne\",\"berry\",\"sparkling\"}', '{\"en\":\"A sophisticated vodka-based punch combining fresh lemon, crème de cassis, and Champagne. This elegant sparkling cocktail features beautiful berry flavors and a refreshing effervescence.\",\"it\":\"Un punch sofisticato a base di vodka che combina limone fresco, crème de cassis e Champagne. Questo elegante cocktail frizzante presenta bellissimi sapori di bacche e un''effervescenza rinfrescante.\",\"vi\":\"Một loại punch tinh tế dựa trên vodka kết hợp chanh tươi, crème de cassis và Champagne. Cocktail có ga thanh lịch này có hương vị quả mọng đẹp mắt và độ sủi bọt sảng khoái.\"}'::jsonb, '{\"created_year\":\"1984\",\"origin\":{\"city\":\"London\",\"bar\":\"Trader Vic''s\",\"country\":\"UK\"},\"creator\":{\"name\":\"Dick Bradsell\",\"profession\":\"bartender\"},\"story\":{\"en\":\"Created by legendary bartender Dick Bradsell in 1984 at Trader Vic''s in London. Bradsell wanted to create a sophisticated vodka cocktail with Champagne that would appeal to elegant drinkers. The drink combines the strength of vodka with the elegance of Champagne and the fruity depth of cassis. It became one of Bradsell''s signature creations and helped establish his reputation as one of the most influential bartenders of the modern cocktail era.\",\"it\":\"Creato dal leggendario barman Dick Bradsell nel 1984 al Trader Vic''s di Londra. Bradsell voleva creare un cocktail vodka sofisticato con Champagne che avrebbe attratto bevitori eleganti. La bevanda combina la forza della vodka con l''eleganza dello Champagne e la profondità fruttata del cassis. Divenne una delle creazioni firma di Bradsell e contribuì a stabilire la sua reputazione come uno dei barman più influenti dell''era dei cocktail moderni.\",\"vi\":\"Được tạo ra bởi bartender huyền thoại Dick Bradsell năm 1984 tại Trader Vic''s ở London. Bradsell muốn tạo ra một cocktail vodka tinh tế với Champagne sẽ thu hút những người uống thanh lịch. Thức uống kết hợp sức mạnh của vodka với sự thanh lịch của Champagne và độ sâu trái cây của cassis. Nó trở thành một trong những sáng tạo đặc trưng của Bradsell và giúp thiết lập danh tiếng của ông như một trong những bartender có ảnh hưởng nhất của thời đại cocktail hiện đại.\"},\"named_after\":{\"en\":\"Named \"Russian\" for vodka, \"Spring\" for its fresh, seasonal character, and \"Punch\" for its complex, multi-ingredient nature.\",\"it\":\"Chiamato \"Russian\" per la vodka, \"Spring\" per il suo carattere fresco e stagionale, e \"Punch\" per la sua natura complessa e multi-ingrediente.\",\"vi\":\"Được đặt tên \"Russian\" cho vodka, \"Spring\" cho tính cách tươi mát theo mùa, và \"Punch\" cho bản chất phức tạp, nhiều nguyên liệu.\"}}'::jsonb, '{\"profile\":[\"sparkling\",\"fruity\",\"elegant\"],\"description\":{\"en\":\"Bright lemon tartness balanced with sweet-tart cassis, vodka smoothness, and Champagne elegance. Complex berry flavors with refreshing bubbles.\",\"it\":\"Acidità brillante del limone bilanciata con cassis dolce-aspro, morbidezza della vodka ed eleganza dello Champagne. Sapori complessi di bacche con bollicine rinfrescanti.\",\"vi\":\"Vị chua chanh tươi mát cân bằng với cassis ngọt-chua, độ mượt vodka và sự thanh lịch Champagne. Hương vị quả mọng phức tạp với bong bóng sảng khoái.\"},\"first_impression\":{\"en\":\"Bright citrus and berry with sparkling effervescence\",\"it\":\"Agrumi e bacche brillanti con effervescenza frizzante\",\"vi\":\"Cam quýt và quả mọng tươi sáng với độ sủi bọt\"},\"finish\":{\"en\":\"Long, elegant finish with lingering berry and citrus notes\",\"it\":\"Finale lungo ed elegante con note persistenti di bacche e agrumi\",\"vi\":\"Kết thúc dài, thanh lịch với hương quả mọng và cam quýt kéo dài\"},\"balance\":{\"en\":\"Perfectly balanced between citrus tartness, berry sweetness, and Champagne elegance\",\"it\":\"Perfettamente bilanciato tra acidità degli agrumi, dolcezza delle bacche ed eleganza dello Champagne\",\"vi\":\"Cân bằng hoàn hảo giữa vị chua cam quýt, vị ngọt quả mọng và sự thanh lịch Champagne\"}}'::jsonb, '{\"best_time\":[\"afternoon\",\"evening\"],\"occasions\":[\"celebration\",\"brunch\",\"garden_party\",\"date_night\"],\"seasons\":[\"spring\",\"summer\"],\"food_pairings\":{\"en\":\"Excellent with berry desserts, light seafood, smoked salmon canapés, or as a celebratory aperitif.\",\"it\":\"Eccellente con dessert ai frutti di bosco, pesce leggero, canapè al salmone affumicato, o come aperitivo celebrativo.\",\"vi\":\"Tuyệt vời với món tráng miệng quả mọng, hải sản nhẹ, canapé cá hồi hun khói, hoặc làm aperitif lễ hội.\"},\"ideal_for\":{\"en\":\"Perfect for vodka and Champagne lovers seeking an elegant, fruity cocktail. Ideal for celebrations or sophisticated brunches.\",\"it\":\"Perfetto per gli amanti della vodka e dello Champagne che cercano un cocktail elegante e fruttato. Ideale per celebrazioni o brunch sofisticati.\",\"vi\":\"Hoàn hảo cho người yêu vodka và Champagne tìm kiếm cocktail thanh lịch, trái cây. Lý tưởng cho lễ kỷ niệm hoặc bữa brunch tinh tế.\"}}'::jsonb, '[{\"ingredient_id\":\"ING_VODKA\",\"quantity\":{\"amount\":25,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Vodka\",\"it\":\"Vodka\",\"vi\":\"Vodka\"}},{\"ingredient_id\":\"ING_LEMON_JUICE\",\"quantity\":{\"amount\":25,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Fresh lemon juice\",\"it\":\"Succo di limone fresco\",\"vi\":\"Nước chanh tươi\"}},{\"ingredient_id\":\"ING_CREME_DE_CASSIS\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Crème de cassis\",\"it\":\"Crème de cassis\",\"vi\":\"Crème de cassis\"}},{\"ingredient_id\":\"ING_SIMPLE_SYRUP\",\"quantity\":{\"amount\":10,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Simple syrup\",\"it\":\"Sciroppo semplice\",\"vi\":\"Siro đường\"}},{\"ingredient_id\":\"ING_CHAMPAGNE\",\"quantity\":{\"amount\":90,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Champagne\",\"it\":\"Champagne\",\"vi\":\"Champagne\"}]'::jsonb, 'shake', '{\"en\":\"Shake vodka, lemon juice, crème de cassis, and simple syrup with ice. Strain into a highball glass filled with ice. Top with Champagne.\",\"it\":\"Shakerare vodka, succo di limone, crème de cassis e sciroppo semplice con ghiaccio. Filtrare in un bicchiere highball pieno di ghiaccio. Completare con Champagne.\",\"vi\":\"Lắc vodka, nước chanh, crème de cassis và siro đường với đá. Lọc vào ly highball đầy đá. Phủ Champagne lên trên.\"}'::jsonb, 'Highball glass', '{\"en\":\"Fresh berries and lemon slice\",\"it\":\"Bacche fresche e fetta di limone\",\"vi\":\"Quả mọng tươi và lát chanh\"}'::jsonb, 'cubed', 'on_the_rocks', '{\"ING_VODKA\"}', '{\"sparkling\",\"fruity\",\"elegant\"}', 12, 190, 'easy', 60, '{\"sulphites\"}', '{\"alcohol\",\"sulphites_intolerance\"}', '{\"vegetarian\",\"vegan\",\"pescatarian\",\"gluten_free\",\"dairy_free\",\"nut_free\"}', 0, '{\"vegan\",\"gluten-free\",\"dairy-free\"}', '{\"spring\",\"summer\"}', '{\"celebration\",\"brunch\",\"garden_party\"}', false, false, '{}', 'Add Champagne last to preserve carbonation. Fresh berries enhance presentation. Can substitute Prosecco for Champagne.', 'high', 65, 'https://iba-world.com/russian-spring-punch/', 'IBA Official Recipe', 1)
