-- 167. Kamikaze
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-6f78-9012-bc23-de45fg678901', 'kamikaze', 'kamikaze_shot_famous_citrus_vodka', '{\"en\":\"Kamikaze\",\"it\":\"Kamikaze\",\"vi\":\"Kamikaze\"}'::jsonb, 'famous', NULL, '{\"shot\",\"shooter\",\"famous\",\"citrus\",\"vodka\",\"sour\"}', '{\"en\":\"A powerful and tart shooter made with vodka, triple sec, and lime juice. This classic shot delivers a strong citrus punch that lives up to its bold name.\",\"it\":\"Uno shooter potente e aspro fatto con vodka, triple sec e succo di lime. Questo shot classico offre un forte pugno di agrumi che è all''altezza del suo nome audace.\",\"vi\":\"Một loại shooter mạnh mẽ và chua được làm từ vodka, triple sec và nước cốt chanh. Shot cổ điển này mang đến cú đấm cam chanh mạnh mẽ xứng đáng với cái tên táo bạo của nó.\"}'::jsonb, '{\"created_year\":\"1970s\",\"origin\":{\"city\":\"Unknown\",\"bar\":\"Unknown\",\"country\":\"USA\"},\"story\":{\"en\":\"The Kamikaze shot emerged in the United States during the 1970s. Its exact origins are unclear, but it quickly became a popular shooter at bars and nightclubs. The drink is essentially a lime-forward variation of the classic Sidecar cocktail, adapted into shot form.\",\"it\":\"Lo shot Kamikaze è emerso negli Stati Uniti durante gli anni ''70. Le sue origini esatte non sono chiare, ma è diventato rapidamente uno shooter popolare nei bar e nelle discoteche. La bevanda è essenzialmente una variazione con lime del classico cocktail Sidecar, adattata in forma di shot.\",\"vi\":\"Shot Kamikaze xuất hiện ở Hoa Kỳ trong những năm 1970. Nguồn gốc chính xác của nó không rõ ràng, nhưng nó nhanh chóng trở thành shooter phổ biến tại các quán bar và hộp đêm. Thức uống về cơ bản là một biến thể hướng chanh của cocktail Sidecar cổ điển, được điều chỉnh thành dạng shot.\"},\"named_after\":{\"en\":\"Named after the Japanese kamikaze pilots of World War II, reflecting the shot''s bold and intense nature.\",\"it\":\"Prende il nome dai piloti kamikaze giapponesi della seconda guerra mondiale, riflettendo la natura audace e intensa dello shot.\",\"vi\":\"Được đặt theo tên của các phi công kamikaze Nhật Bản trong Thế chiến II, phản ánh bản chất táo bạo và mạnh mẽ của shot.\"}}'::jsonb, '{\"profile\":[\"sour\",\"citrus\",\"strong\"],\"description\":{\"en\":\"Sharp and citrus-forward with a clean vodka backbone. The lime juice provides tartness while triple sec adds subtle orange sweetness. Bold and refreshing.\",\"it\":\"Deciso e orientato agli agrumi con una base pulita di vodka. Il succo di lime fornisce acidità mentre il triple sec aggiunge una sottile dolcezza all''arancia. Audace e rinfrescante.\",\"vi\":\"Sắc nét và hướng cam chanh với nền vodka trong sạch. Nước cốt chanh mang đến vị chua trong khi triple sec thêm vị ngọt cam tinh tế. Táo bạo và sảng khoái.\"},\"first_impression\":{\"en\":\"Sharp lime tartness with immediate vodka bite\",\"it\":\"Acidità decisa del lime con morso immediato di vodka\",\"vi\":\"Vị chua chanh sắc nét với độ gắt vodka ngay lập tức\"},\"finish\":{\"en\":\"Clean, crisp finish with lingering citrus\",\"it\":\"Finale pulito e fresco con agrumi persistenti\",\"vi\":\"Kết thúc trong sạch, sắc nét với cam chanh kéo dài\"},\"balance\":{\"en\":\"Tart and strong, not meant to be subtle\",\"it\":\"Aspro e forte, non destinato ad essere sottile\",\"vi\":\"Chua và mạnh, không có ý nghĩa tinh tế\"}}'::jsonb, '{\"best_time\":[\"evening\",\"late_night\"],\"occasions\":[\"party\",\"nightlife\",\"celebration\"],\"seasons\":[\"all_seasons\"],\"food_pairings\":{\"en\":\"Best as a standalone shot. Can be paired with salty snacks, nachos, or Asian appetizers to balance the tartness.\",\"it\":\"Meglio come shot standalone. Può essere abbinato a snack salati, nachos o antipasti asiatici per bilanciare l''acidità.\",\"vi\":\"Tốt nhất như một shot độc lập. Có thể kết hợp với đồ ăn vặt mặn, nachos hoặc món khai vị châu Á để cân bằng vị chua.\"},\"ideal_for\":{\"en\":\"Perfect for vodka lovers who enjoy tart, citrus-forward shots. A party favorite for those who appreciate bold flavors.\",\"it\":\"Perfetto per gli amanti della vodka che apprezzano gli shot aspri e orientati agli agrumi. Un favorito delle feste per chi apprezza i sapori audaci.\",\"vi\":\"Hoàn hảo cho người yêu vodka thích shot chua, hướng cam chanh. Được ưa chuộng trong tiệc cho những ai đánh giá cao hương vị táo bạo.\"}}'::jsonb, '[{\"ingredient_id\":\"ING_VODKA\",\"quantity\":{\"amount\":30,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Vodka\",\"it\":\"Vodka\",\"vi\":\"Vodka\"}},{\"ingredient_id\":\"ING_TRIPLE_SEC\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Triple Sec\",\"it\":\"Triple Sec\",\"vi\":\"Triple Sec\"}},{\"ingredient_id\":\"ING_LIME_JUICE\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Fresh Lime Juice\",\"it\":\"Succo di Lime Fresco\",\"vi\":\"Nước Cốt Chanh Tươi\"}]'::jsonb, 'shake', '{\"en\":\"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a shot glass and serve immediately.\",\"it\":\"Aggiungere tutti gli ingredienti in uno shaker pieno di ghiaccio. Shakerare vigorosamente fino a raffreddare bene. Filtrare in un bicchierino da shot e servire immediatamente.\",\"vi\":\"Thêm tất cả nguyên liệu vào bình lắc đầy đá. Lắc mạnh cho đến khi lạnh hoàn toàn. Lọc vào ly shot và phục vụ ngay lập tức.\"}'::jsonb, 'Shot glass', '{\"en\":\"Lime wheel (optional)\",\"it\":\"Fettina di lime (opzionale)\",\"vi\":\"Lát chanh (tùy chọn)\"}'::jsonb, 'none', 'shot', '{\"ING_VODKA\"}', '{\"sour\",\"citrus\",\"strong\"}', 28, 120, 'easy', 45, '{\"sulphites\"}', '{\"alcohol\",\"sulphites_intolerance\"}', '{\"vegan\",\"vegetarian\",\"pescatarian\",\"gluten_free\",\"dairy_free\",\"nut_free\"}', 0, '{\"vegan\",\"vegetarian\",\"gluten-free\",\"dairy-free\"}', '{\"all-seasons\"}', '{\"party\",\"nightlife\",\"celebration\"}', false, false, '{\"blue-kamikaze\",\"frozen-kamikaze\",\"kamikaze-cocktail\"}', 'Always use fresh lime juice, never bottled. Shake hard to ensure proper dilution and temperature. Can also be served as a cocktail in a martini glass.', 'low', 80, 'https://en.wikipedia.org/wiki/Kamikaze_(cocktail)', 'Classic shooter recipe from the 1970s bar scene.', 1)
