-- 195. Red Headed Slut
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e5f6g7h8-9012-3456-ef56-gh78ij901234', 'red-headed-slut', 'red_headed_slut_shot_famous_peach_cranberry', '{\"en\":\"Red Headed Slut\",\"it\":\"Red Headed Slut\",\"vi\":\"Red Headed Slut\"}'::jsonb, 'famous', NULL, '{\"shot\",\"shooter\",\"famous\",\"fruity\",\"sweet\",\"party\"}', '{\"en\":\"A sweet and fruity shooter combining peach schnapps, Jägermeister, and cranberry juice. Despite its provocative name, this popular party shot delivers a surprisingly smooth and sweet flavor that masks its alcoholic strength.\",\"it\":\"Uno shooter dolce e fruttato che combina peach schnapps, Jägermeister e succo di mirtillo rosso. Nonostante il suo nome provocatorio, questo popolare shot da festa offre un sapore sorprendentemente morbido e dolce che maschera la sua forza alcolica.\",\"vi\":\"Một loại shooter ngọt và trái cây kết hợp peach schnapps, Jägermeister và nước ép nam việt quất. Mặc dù có cái tên khiêu khích, shot tiệc phổ biến này mang đến hương vị ngọt và mượt mà đáng ngạc nhiên che giấu độ mạnh của rượu.\"}'::jsonb, '{\"created_year\":\"1980s\",\"origin\":{\"city\":\"Unknown\",\"bar\":\"Unknown\",\"country\":\"USA\"},\"story\":{\"en\":\"The Red Headed Slut emerged in American bars during the 1980s party scene. Its exact origin is disputed, but the shot gained widespread popularity in college towns and nightclubs. The name, while controversial, became part of its identity and contributed to its notoriety as a fun, party-oriented drink.\",\"it\":\"Il Red Headed Slut è emerso nei bar americani durante la scena delle feste degli anni ''80. La sua origine esatta è controversa, ma lo shot ha guadagnato ampia popolarità nelle città universitarie e nei nightclub. Il nome, sebbene controverso, è diventato parte della sua identità e ha contribuito alla sua notorietà come bevanda divertente orientata alle feste.\",\"vi\":\"Red Headed Slut xuất hiện tại các quầy bar Mỹ trong bối cảnh tiệc tung những năm 1980. Nguồn gốc chính xác của nó còn tranh cãi, nhưng shot này đã trở nên phổ biến rộng rãi ở các thị trấn đại học và hộp đêm. Cái tên, mặc dù gây tranh cãi, đã trở thành một phần của bản sắc và góp phần vào sự nổi tiếng như một thức uống vui vẻ, hướng đến tiệc tung.\"},\"named_after\":{\"en\":\"The name is a provocative reference to the drink''s reddish color from cranberry juice.\",\"it\":\"Il nome è un riferimento provocatorio al colore rossastro della bevanda dal succo di mirtillo rosso.\",\"vi\":\"Cái tên là một tham chiếu khiêu khích đến màu đỏ của thức uống từ nước ép nam việt quất.\"}}'::jsonb, '{\"profile\":[\"sweet\",\"fruity\",\"herbal\"],\"description\":{\"en\":\"Surprisingly sweet with peach and cranberry flavors dominating. The Jägermeister adds herbal complexity without overwhelming bitterness. Smooth and easy to drink despite the strong alcohol content.\",\"it\":\"Sorprendentemente dolce con sapori di pesca e mirtillo rosso dominanti. Il Jägermeister aggiunge complessità erbacea senza amarezza travolgente. Morbido e facile da bere nonostante il forte contenuto alcolico.\",\"vi\":\"Ngọt đáng ngạc nhiên với hương vị đào và nam việt quất chiếm ưu thế. Jägermeister thêm sự phức tạp thảo mộc mà không có vị đắng quá mức. Mượt và dễ uống mặc dù có hàm lượng cồn cao.\"},\"first_impression\":{\"en\":\"Sweet peach and cranberry with subtle herbal notes\",\"it\":\"Pesca dolce e mirtillo rosso con note erbacee sottili\",\"vi\":\"Đào ngọt và nam việt quất với hương thảo mộc tinh tế\"},\"finish\":{\"en\":\"Smooth finish with lingering peach sweetness and herbal warmth\",\"it\":\"Finale morbido con dolcezza persistente di pesca e calore erbacea\",\"vi\":\"Kết thúc mượt mà với vị ngọt đào kéo dài và hơi ấm thảo mộc\"},\"balance\":{\"en\":\"Sweet and fruity flavors balance the herbal intensity of Jägermeister\",\"it\":\"I sapori dolci e fruttati bilanciano l''intensità erbacea del Jägermeister\",\"vi\":\"Hương vị ngọt và trái cây cân bằng cường độ thảo mộc của Jägermeister\"}}'::jsonb, '{\"best_time\":[\"evening\",\"late_night\"],\"occasions\":[\"party\",\"nightlife\",\"celebration\",\"bachelor_bachelorette\"],\"seasons\":[\"all_seasons\"],\"food_pairings\":{\"en\":\"Best enjoyed as a party shot without food pairings. Can be followed by light snacks or appetizers.\",\"it\":\"Meglio gustato come shot da festa senza abbinamenti alimentari. Può essere seguito da snack leggeri o antipasti.\",\"vi\":\"Thưởng thức tốt nhất như một shot tiệc mà không cần kết hợp thức ăn. Có thể theo sau bằng đồ ăn nhẹ hoặc món khai vị.\"},\"ideal_for\":{\"en\":\"Perfect for party-goers who enjoy sweet, easy-drinking shots. A popular choice for those who want to mask the taste of strong alcohol.\",\"it\":\"Perfetto per gli amanti delle feste che apprezzano gli shot dolci e facili da bere. Una scelta popolare per coloro che vogliono mascherare il gusto dell''alcol forte.\",\"vi\":\"Hoàn hảo cho những người đi tiệc thích shot ngọt, dễ uống. Lựa chọn phổ biến cho những ai muốn che giấu vị của rượu mạnh.\"}}'::jsonb, '[{\"ingredient_id\":\"ING_JAGERMEISTER\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Jägermeister\",\"it\":\"Jägermeister\",\"vi\":\"Jägermeister\"}},{\"ingredient_id\":\"ING_PEACH_SCHNAPPS\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Peach Schnapps\",\"it\":\"Peach Schnapps\",\"vi\":\"Peach Schnapps\"}},{\"ingredient_id\":\"ING_CRANBERRY_JUICE\",\"quantity\":{\"amount\":15,\"unit\":\"ml\"},\"display_name\":{\"en\":\"Cranberry Juice\",\"it\":\"Succo di Mirtillo Rosso\",\"vi\":\"Nước Ép Nam Việt Quất\"}]'::jsonb, 'shake', '{\"en\":\"Add all ingredients to a cocktail shaker filled with ice. Shake well until chilled. Strain into a shot glass and serve immediately.\",\"it\":\"Aggiungere tutti gli ingredienti in uno shaker pieno di ghiaccio. Shakerare bene fino a raffreddare. Filtrare in un bicchierino da shot e servire immediatamente.\",\"vi\":\"Thêm tất cả nguyên liệu vào bình lắc đầy đá. Lắc kỹ cho đến khi lạnh. Lọc vào ly shot và phục vụ ngay lập tức.\"}'::jsonb, 'Shot glass', '{\"en\":\"None\",\"it\":\"Nessuna\",\"vi\":\"Không\"}'::jsonb, 'none', 'shot', '{\"ING_JAGERMEISTER\",\"ING_PEACH_SCHNAPPS\"}', '{\"sweet\",\"fruity\",\"herbal\"}', 24, 100, 'easy', 40, '{\"sulphites\"}', '{\"alcohol\",\"sulphites_intolerance\"}', '{\"vegan\",\"vegetarian\",\"pescatarian\",\"gluten_free\",\"dairy_free\",\"nut_free\"}', 0, '{\"vegan\",\"vegetarian\",\"gluten-free\",\"dairy-free\"}', '{\"all-seasons\"}', '{\"party\",\"nightlife\",\"celebration\"}', false, false, '{\"ginger-headed-slut\",\"blonde-headed-slut\",\"brunette-slut\"}', 'Despite the provocative name, this is a popular and requested shot. Some venues may use alternative names like \"Ginger Shot\" or \"Peachy Keen\" to avoid controversy. Shake well to ensure proper mixing.', 'low', 70, 'https://www.liquor.com/recipes/red-headed-slut/', 'Classic party shot from the 1980s bar scene.', 1)
