INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('2246e1f9-5cb3-4613-9580-ffaeb53069ab', 'angel-face', 'd000a774765e8b94bb4687d77620ca8a6ea95d21', '{"en":"Angel Face"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","fruity","strong","prohibition-era"}', '{"en":"An elegant Prohibition-era cocktail combining gin with apple brandy and apricot liqueur. Deceptively smooth and dangerously potent, the Angel Face earned its name for its innocent appearance that masks a powerful punch."}'::jsonb, '{"created_year":"1930","origin":{"city":"Paris or London","country":"France/UK"},"story":{"en":"The Angel Face emerged during the Prohibition era and first appeared in the 1930 Savoy Cocktail Book compiled by Harry Craddock at London''s Savoy Hotel. Its name is a playful warning: despite its smooth, fruity taste and angelic appearance, this cocktail is deceptively strong with no non-alcoholic ingredients to dilute its potency. The equal-parts formula makes it easy to remember but dangerous to underestimate."},"named_after":{"en":"Named for its deceptively innocent, smooth taste that belies its high alcohol content - a true \"wolf in sheep''s clothing\" cocktail."}}'::jsonb, '{"profile":["fruity","sweet","boozy","dry"],"description":{"en":"Fruity and deceptively smooth. The apricot brandy provides stone fruit sweetness while the Calvados adds apple complexity and the gin brings botanical dryness. Together they create a harmonious but potent sipper."},"first_impression":{"en":"Sweet apricot and apple notes greet the palate"},"finish":{"en":"Long, warming finish with lingering fruit and spirit"},"balance":{"en":"Three equal parts create perfect harmony between fruit sweetness and botanical dryness"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","date_night","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Pairs well with fruit-based desserts, apple tarts, or as a digestif after a rich meal. The apple and apricot notes complement cheese plates."},"ideal_for":{"en":"Gin lovers seeking something different, fans of fruit-forward cocktails who enjoy a strong drink. Not for lightweights - this is a serious cocktail despite its innocent name."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_APRICOT_BRANDY","quantity":{"amount":30,"unit":"ml"},"display_name":"Apricot Brandy","notes":null},{"ingredient_id":"ING_CALVADOS","quantity":{"amount":30,"unit":"ml"},"display_name":"Calvados","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice cubes. Shake well until properly chilled. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass', '{"en":"None (traditionally served without garnish)"}'::jsonb, 'none', 'up', '{"ING_GIN_LONDON_DRY","ING_CALVADOS"}', '{"fruity","sweet","boozy","dry"}', 30, 220, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"digestivo","date_night","celebration"}', false, false, '{}', 'Warn guests this is a strong cocktail (100% alcohol, no mixers). The equal parts ratio makes it easy to scale. Use quality Calvados for best results.', 'mid', 45, 'https://iba-world.com/angel-face/', 'IBA Official Recipe. First published in Savoy Cocktail Book 1930.', 1)
