-- 4. Aviation
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('01563d2f-07b9-4049-aa29-d63c854f831a', 'aviation', '6b7736cd1ee920f1f015a5b03d90364f7442efba', '{"en":"Aviation"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","floral","pre-prohibition","elegant"}', '{"en":"A stunning pre-Prohibition classic with a distinctive pale sky-blue color from crème de violette. The Aviation captures the wonder and romance of early flight with its ethereal appearance and delicate floral-citrus balance."}'::jsonb, '{"created_year":1916,"origin":{"city":"New York City","bar":"Hotel Wallick","country":"USA"},"creator":{"name":"Hugo Ensslin","profession":"head bartender"},"story":{"en":"The Aviation was created by Hugo Ensslin, head bartender at the Hotel Wallick in New York City, and published in his 1916 book \"Recipes for Mixed Drinks.\" The cocktail was named to honor the pioneers of aviation during the exciting early days of flight. The crème de violette gives it a pale blue color reminiscent of the sky. When Harry Craddock included it in the Savoy Cocktail Book (1930), he omitted the crème de violette, and for decades the \"colorless\" version became standard. The original violet-tinted recipe was rediscovered in the craft cocktail renaissance of the 2000s."},"named_after":{"en":"Named to honor early aviation pioneers. The pale blue color from crème de violette represents the sky, making it a tribute to the romance of flight."}}'::jsonb, '{"profile":["floral","citrus","dry","herbal"],"description":{"en":"Delicate and sophisticated. The gin provides the juniper backbone while maraschino adds subtle cherry-almond complexity. The lemon brings bright acidity and the crème de violette contributes ethereal floral notes without overwhelming."},"first_impression":{"en":"Bright lemon and subtle violet florals"},"finish":{"en":"Clean, dry finish with lingering floral elegance"},"balance":{"en":"Perfectly balanced between citrus brightness, floral delicacy, and gin''s botanical complexity"}}'::jsonb, '{"best_time":["evening"],"occasions":["date_night","celebration","aperitivo"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent aperitif before seafood or light fare. The floral notes complement dishes with herbs like lavender or thyme. Pairs beautifully with oysters or light canapés."},"ideal_for":{"en":"Cocktail connoisseurs who appreciate subtle flavors and historical drinks. Perfect for gin lovers seeking something elegant and unusual. A must-try for anyone interested in craft cocktail history."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":15,"unit":"ml"},"display_name":"Maraschino Luxardo","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_CREME_DE_VIOLETTE","quantity":{"amount":5,"unit":"ml"},"display_name":"Crème de Violette","notes":"1 bar spoon"}]'::jsonb, 'shake', '{"en":"Add all ingredients into a cocktail shaker. Shake with cracked ice until well chilled. Strain into a chilled cocktail glass. Optionally garnish with a maraschino cherry."}'::jsonb, 'Cocktail glass', '{"en":"Optional maraschino cherry"}'::jsonb, 'none', 'up', '{"ING_GIN_LONDON_DRY"}', '{"floral","citrus","dry","herbal"}', 24, 165, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"date_night","celebration","aperitivo"}', false, false, '{"aviation-without-violette"}', 'The crème de violette is essential for the authentic sky-blue color - don''t skip it. Use Luxardo maraschino, not cheap imitations. Use sparingly - too much violette makes it taste like perfume. A tiny amount goes a long way.', 'mid', 72, 'https://iba-world.com/aviation/', 'IBA Official Recipe. Originally from Hugo Ensslin''s 1916 "Recipes for Mixed Drinks."', 1)
