-- 6. Between the Sheets
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('efac4178-caa9-4510-8d17-12086e06c8c4', 'between-the-sheets', 'between-the-sheets-iba-unforgettable', '{"en":"Between the Sheets"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","strong","prohibition-era","citrus","sidecar-variation"}', '{"en":"A potent Prohibition-era cocktail that''s essentially a Sidecar with rum added. The suggestive name and smooth, citrusy taste made it a favorite during the roaring twenties. Three spirits unite with orange and lemon for a sophisticated, dangerously drinkable classic."}'::jsonb, '{"created_year":"1920s","origin":{"city":"Paris","bar":"Harry''s New York Bar","country":"France"},"creator":{"name":"Harry MacElhone","profession":"bartender"},"story":{"en":"Between the Sheets was created at Harry''s New York Bar in Paris during the 1920s Prohibition era, when Americans flocked to Europe to drink legally. The cocktail is essentially a variation of the Sidecar (also born at Harry''s Bar) with white rum added to the mix. The provocative name was typical of the era''s playful attitude toward drinking. Some attribute it to a French bartender who created it as a \"nightcap\" - hence the bedroom reference."},"named_after":{"en":"The suggestive name references bedsheets, playing on the cocktail''s reputation as a seductive nightcap. The smooth taste makes it dangerously easy to have \"one more.\""}}'::jsonb, '{"profile":["citrus","boozy","dry","fruity"],"description":{"en":"Bright and citrus-forward with serious spirit backbone. The cognac brings depth and warmth, the rum adds smoothness and subtle sweetness, while the triple sec and lemon provide bright, zesty freshness. Deceptively smooth for its strength."},"first_impression":{"en":"Bright citrus burst with warming spirit notes"},"finish":{"en":"Long, warming finish with orange and brandy lingering"},"balance":{"en":"Equal parts spirits create complexity while citrus keeps it fresh and drinkable"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["date_night","nightcap","party"],"seasons":["all_year"],"food_pairings":{"en":"Best enjoyed as an after-dinner drink or nightcap. If pairing with food, consider light desserts with citrus elements or dark chocolate."},"ideal_for":{"en":"Those who enjoy Sidecars and want something with more complexity. Perfect for anyone seeking a sophisticated, strong cocktail with a romantic backstory. Not for the faint of heart."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White Rum","notes":null},{"ingredient_id":"ING_COGNAC","quantity":{"amount":30,"unit":"ml"},"display_name":"Cognac","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":30,"unit":"ml"},"display_name":"Triple Sec","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients into a cocktail shaker. Shake with ice until well chilled. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass', '{"en":"None (traditionally served without garnish)"}'::jsonb, 'none', 'up', '{"ING_RUM_WHITE","ING_COGNAC"}', '{"citrus","boozy","dry","fruity"}', 28, 210, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all_year"}', '{"date_night","nightcap","party"}', false, false, '{"sidecar"}', 'Very strong cocktail - warn guests. Think of it as a boozy Sidecar. Use quality cognac and fresh lemon juice. Some variations use Cointreau instead of triple sec for a richer orange flavor.', 'mid', 55, 'https://iba-world.com/between-the-sheets/', 'IBA Official Recipe. Originated at Harry''s New York Bar, Paris, 1920s.', 1)
