-- 7. Boulevardier
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('83348635-90bd-4080-af0f-835b682c0456', 'boulevardier', '3b9e4e191f4d57d46e0a225063c3b8818aa84c5d', '{"en":"Boulevardier"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","bitter","whiskey","negroni-variation","prohibition-era"}', '{"en":"The whiskey-lover''s Negroni - a rich, bittersweet cocktail that swaps gin for bourbon or rye. Created by an American expatriate in Paris, the Boulevardier offers deeper, warmer flavors while maintaining the perfect bitter-sweet balance."}'::jsonb, '{"created_year":1927,"origin":{"city":"Paris","bar":"Harry''s New York Bar","country":"France"},"creator":{"name":"Erskine Gwynne","profession":"writer and socialite"},"story":{"en":"The Boulevardier was created by Erskine Gwynne, an American writer and nephew of railroad magnate Alfred Vanderbilt, who founded a monthly magazine called \"Boulevardier\" in Paris in the 1920s. The cocktail was first published in Harry MacElhone''s 1927 book \"Barflies and Cocktails.\" Gwynne was a fixture of the expatriate scene in Paris during Prohibition, when Americans fled to Europe to drink legally. The drink is essentially a Negroni with bourbon replacing the gin, reflecting American taste. The name \"boulevardier\" means \"a man about town\" in French."},"named_after":{"en":"Named after the magazine \"Boulevardier\" founded by its creator Erskine Gwynne. The French word means \"a man about town\" - someone who frequents fashionable places."}}'::jsonb, '{"profile":["bitter","sweet","boozy","herbal"],"description":{"en":"Richer and more robust than a Negroni. The bourbon/rye provides caramel, vanilla, and oak notes that complement Campari''s bitterness beautifully. The sweet vermouth adds herbal complexity. A warming, sophisticated sipper."},"first_impression":{"en":"Warm whiskey sweetness meeting bold Campari bitterness"},"finish":{"en":"Long, warming finish with lingering bitter-orange and oak"},"balance":{"en":"The whiskey''s sweetness counterbalances Campari''s bitterness, with vermouth as the bridge"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["aperitivo","digestivo","date_night"],"seasons":["autumn","winter"],"food_pairings":{"en":"Excellent with rich meats, charcuterie, and aged cheeses. The bitter notes complement fatty foods beautifully. Also pairs well with dark chocolate."},"ideal_for":{"en":"Whiskey lovers who want to explore bitter cocktails. Perfect for those who find Negronis too gin-forward. A sophisticated choice for cooler weather."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":45,"unit":"ml"},"display_name":"Bourbon or Rye Whiskey","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":30,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet Vermouth","notes":null}]'::jsonb, 'stir', '{"en":"Pour all ingredients into a mixing glass with ice cubes. Stir well until properly chilled and diluted. Strain into a chilled cocktail glass. Garnish with orange zest."}'::jsonb, 'Cocktail glass', '{"en":"Orange zest, optionally with lemon zest"}'::jsonb, 'none', 'up', '{"ING_BOURBON"}', '{"bitter","sweet","boozy","herbal"}', 25, 185, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","dairy_free","nut_free"}', 0, '{"vegan","dairy-free"}', '{"autumn","winter"}', '{"aperitivo","digestivo","date_night"}', false, false, '{"negroni","old-pal"}', 'Use good bourbon (Bulleit, Buffalo Trace) or rye (Rittenhouse). Some prefer equal parts (1:1:1) for a more bitter drink. Can serve on the rocks for a longer drink. The "whiskey Negroni" nickname helps explain it to guests.', 'mid', 78, 'https://iba-world.com/boulevardier/', 'IBA Official Recipe. First published in "Barflies and Cocktails" 1927.', 1)
