INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a7b8c9d0-e1f2-4a3b-4c5d-6e7f8a9b0c1d', 'brandy-crusta', 'c1d0e9f8a7b6c5d4e3f2a1b0c9d8e7f6a5b4c3d2', '{"en":"Brandy Crusta"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","ornate","historical","new-orleans"}', '{"en":"A historically significant cocktail from 1850s New Orleans, featuring brandy with citrus and maraschino, served in a sugar-crusted glass with an ornate lemon peel spiral. Regarded as the forerunner to the Sidecar and, by extension, the Margarita."}'::jsonb, '{"created_year":"1850s","origin":{"city":"New Orleans","bar":"Jewel of the South (Santini''s bar)","country":"USA"},"creator":{"name":"Joseph Santini","profession":"bartender"},"story":{"en":"The Brandy Crusta was created in the 1850s by Joseph Santini, an Italian from Trieste, in New Orleans. The name refers to the \"crust\" of sugar around the rim. It was one of the first cocktails to use a sugared rim, an innovation that would later become standard in drinks like the Margarita. The Brandy Crusta was added to the IBA official list in the 2020 update as one of 6 drinks added to \"The Unforgettables\" category. The elaborate presentation with a full lemon peel spiral was revolutionary for its time."},"named_after":{"en":"Named after the sugar \"crust\" that rims the glass."}}'::jsonb, '{"profile":["citrus","sweet","complex"],"description":{"en":"Bright and citrus-forward with brandy warmth, balanced by maraschino sweetness and orange complexity. The sugar rim adds textural sweetness while bitters provide aromatic depth."},"first_impression":{"en":"Sweet sugar rim followed by bright citrus and brandy"},"finish":{"en":"Long, warming finish with lingering brandy and citrus notes"},"balance":{"en":"Well-balanced between sweet, sour, and spirit strength"}}'::jsonb, '{"best_time":["evening","after_dinner","special_occasion"],"occasions":["celebration","special_occasion","impressive","digestivo"],"seasons":["autumn","winter","spring"],"food_pairings":{"en":"Excellent as a digestif with desserts, particularly citrus-based desserts, crème brûlée, or fruit tarts. Also pairs with aged cheeses and dark chocolate."},"ideal_for":{"en":"Perfect for brandy enthusiasts and cocktail historians. Ideal for special occasions when you want to impress with both flavor and presentation."}}'::jsonb, '[{"ingredient_id":"ING_BRANDY","quantity":{"amount":52.5,"unit":"ml"},"display_name":"Brandy","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Maraschino Luxardo","notes":null},{"ingredient_id":"ING_CURACAO","quantity":{"amount":5,"unit":"ml"},"display_name":"Curaçao","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":5,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_AROMATIC_BITTERS","quantity":{"amount":2,"unit":"dash"},"display_name":"Aromatic bitters","notes":null}]'::jsonb, 'shake', '{"en":"Rub a slice of orange (or lemon) around the rim of the glass and dip it in pulverized white sugar, so that the sugar will adhere to the edge of the glass. Mix together all ingredients with ice cubes in a mixing glass and strain into a prepared slim cocktail glass."}'::jsonb, 'Cocktail glass (narrow)', '{"en":"Long lemon peel spiral"}'::jsonb, 'none', 'up', '{"ING_BRANDY"}', '{"citrus","sweet","complex"}', 25, 190, 'hard', 180, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring"}', '{"celebration","special_occasion","digestivo"}', false, false, '{"sidecar","margarita","whiskey-crusta"}', 'The presentation is crucial - take time with the sugar rim and lemon spiral. The lemon peel should be cut in one continuous spiral to line the inside of the glass. This is a showpiece cocktail. Use quality brandy or cognac.', 'premium', 40, 'https://iba-world.com/iba-cocktail/brandy-crusta/', 'IBA Official Recipe. Historical information from New Orleans cocktail history and Joseph Santini documentation.', 1)
