-- 11. Daiquiri
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('2218cd73-5e9d-4148-979d-13436b6e2c47', 'daiquiri', 'daiquiri-iba-unforgettable', '{"en":"Daiquiri"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","cuban","rum","sour","refreshing","hemingway"}', '{"en":"The quintessential rum sour - a perfect balance of white rum, fresh lime, and just enough sugar. Simple, elegant, and refreshing, the classic Daiquiri is a testament to the beauty of restraint. Forget the frozen strawberry versions - this is the real deal."}'::jsonb, '{"created_year":1898,"origin":{"city":"Santiago de Cuba","bar":"Venus Hotel / Daiquiri mines","country":"Cuba"},"creator":{"name":"Jennings Cox","profession":"American mining engineer"},"story":{"en":"The Daiquiri was created around 1898 by Jennings Cox, an American mining engineer working at iron mines near the village of Daiquirí in Cuba. Legend says Cox ran out of gin while entertaining American guests and improvised with local rum, lime, and sugar. The cocktail was later popularized at Havana''s El Floridita bar, where it became Ernest Hemingway''s drink of choice. Hemingway allegedly consumed up to 15 double daiquiris in a single sitting (though he preferred his without sugar - the \"Papa Doble\"). The simple recipe epitomizes Cuban cocktail culture."},"named_after":{"en":"Named after Daiquirí, a village and beach near Santiago de Cuba where the iron mines were located."}}'::jsonb, '{"profile":["sour","sweet","citrus","refreshing"],"description":{"en":"Crisp, clean, and perfectly balanced. The white rum provides a clean canvas with subtle sweetness, the lime brings bright acidity, and the sugar rounds it all together. Simple ingredients, complex satisfaction."},"first_impression":{"en":"Bright, zesty lime followed by clean rum sweetness"},"finish":{"en":"Clean, refreshing finish with subtle rum warmth"},"balance":{"en":"The holy trinity of rum cocktails - spirit, citrus, and sugar in perfect harmony"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","party","aperitivo"],"seasons":["spring","summer","all_year"],"food_pairings":{"en":"Perfect with seafood, especially ceviche, oysters, and grilled fish. Also excellent with Cuban cuisine and Caribbean dishes."},"ideal_for":{"en":"Everyone. A true crowd-pleaser that showcases rum''s versatility. Perfect for rum skeptics and enthusiasts alike. The benchmark for all rum sours."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":60,"unit":"ml"},"display_name":"White Cuban Rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh Lime Juice","notes":null},{"ingredient_id":"ING_SUPERFINE_SUGAR","quantity":{"amount":10,"unit":"ml"},"display_name":"Superfine Sugar","notes":"2 bar spoons"}]'::jsonb, 'shake', '{"en":"In a cocktail shaker, add all ingredients. Stir well to dissolve the sugar. Add ice and shake vigorously until well chilled. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass', '{"en":"None (traditionally served without garnish)"}'::jsonb, 'none', 'up', '{"ING_RUM_WHITE"}', '{"sour","sweet","citrus","refreshing"}', 23, 145, 'easy', 45, '{"sulphites"}', '{"alcohol","fructose","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","all_year"}', '{"casual","party","aperitivo"}', false, false, '{"frozen-daiquiri","hemingway-daiquiri","strawberry-daiquiri"}', 'Use quality white rum (Havana Club, Bacardi Superior). Fresh lime juice is non-negotiable. Dissolve sugar before adding ice. This is NOT a frozen drink - that''s a different (and lesser) beast. Offer the Hemingway Daiquiri (Papa Doble) as a drier variation.', 'low', 92, 'https://iba-world.com/daiquiri/', 'IBA Official Recipe. Cuban classic from 1898.', 1)
