-- 16. John Collins
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('ee47d6a4-8259-4123-81ba-b8958f32dd38', 'john-collins', '7ec34e7da35c6b3b8aade19d2ba6c0b3542b9b36', '{"en":"John Collins"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","tall","refreshing"}', '{"en":"A tall, refreshing classic from Victorian London. The John Collins combines gin, fresh lemon juice, sugar, and soda water in a highball glass, creating a light and effervescent drink perfect for any occasion."}'::jsonb, '{"created_year":"1790","origin":{"city":"London","bar":"Limmer''s Old House","country":"United Kingdom"},"creator":{"name":"John Collins","profession":"bartender"},"story":{"en":"The John Collins is believed to have originated with a headwaiter named John Collins who worked at Limmer''s Old House in Conduit Street in Mayfair, a popular London hotel and coffee house around 1790-1817. Limmer''s was described as a chaotic, lively sporting bar with cock fighting, duels, gambling and plenty of drinking. A rhyme by Frank and Charles Sheridan immortalized him: \"My name is John Collins, head waiter at Limmer''s, Corner of Conduit Street, Hanover Square.\" The drink originally used genever (Holland gin), a malty, sweet spirit that predates modern gin. The famous Tom Collins variant emerged when bartenders began using Old Tom gin instead of genever, eventually leading to the name change."},"named_after":{"en":"Named after John Collins, the headwaiter at Limmer''s Old House who created the original drink. The variant \"Tom Collins\" came later when Old Tom gin replaced genever."}}'::jsonb, '{"profile":["citrus","refreshing","effervescent"],"description":{"en":"A bright, zesty cocktail that perfectly balances citrus tartness with gentle sweetness. The gin provides botanical depth while soda water adds refreshing effervescence. Light, crisp, and endlessly drinkable."},"first_impression":{"en":"Immediate bright lemon acidity with the refreshing sparkle of soda. The gin''s botanicals provide complexity without overwhelming the citrus focus."},"finish":{"en":"Clean, refreshing finish with lingering citrus notes. The soda keeps it light and effervescent throughout."},"balance":{"en":"Expertly balanced between sweet and sour with gin providing botanical backbone. The soda dilutes and refreshes without washing out flavor."}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","garden_party","brunch","summer"],"seasons":["spring","summer"],"food_pairings":{"en":"Light seafood, garden salads, grilled fish, lemon chicken, fresh fruit. Perfect alongside picnic foods and summer fare."},"ideal_for":{"en":"Anyone seeking a refreshing, easy-drinking cocktail. Perfect for warm weather, outdoor gatherings, or as a revitalizing afternoon drink."}}'::jsonb, '[{"ingredient_id":"ING_GIN_LONDON_DRY","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple Syrup","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":60,"unit":"ml"},"display_name":"Soda Water","notes":null}]'::jsonb, 'build', '{"en":"Pour all ingredients directly into highball glass filled with ice. Stir gently to combine."}'::jsonb, 'Highball glass', '{"en":"Lemon slice and maraschino cherry"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_GIN_LONDON_DRY"}', '{"citrus","refreshing","effervescent","light"}', 9, 165, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","garden_party","brunch"}', false, false, '{"Tom Collins","Vodka Collins","Whiskey Collins"}', 'Always use fresh-squeezed lemon juice. Build directly in glass to maintain carbonation. Add soda last and stir gently. The IBA notes to use Old Tom gin for a Tom Collins variant. Keep garnishes fresh.', 'mid', 70, 'https://iba-world.com/john-collins/', 'IBA Official Recipe.', 1)
