-- 19. Margarita
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('671fef4d-8461-4367-9710-3a767a8c904c', 'margarita', 'margarita', '{"en":"Margarita"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","classic","shaken","sour"}', '{"en":"The Margarita is the world''s most popular tequila cocktail. A perfect balance of tequila, triple sec, and fresh lime juice, often served with a signature salt rim. Refreshing, tangy, and unmistakably Mexican."}'::jsonb, '{"created_year":"1938","origin":{"city":"Tijuana / Rosarito","bar":"Rancho La Gloria","country":"Mexico"},"creator":{"name":"Carlos \"Danny\" Herrera","profession":"bartender"},"story":{"en":"The Margarita''s origins are shrouded in mystery with numerous competing claims. The most popular story credits Carlos \"Danny\" Herrera, who created it in 1938 at his restaurant Rancho La Gloria for former Ziegfeld dancer Marjorie King, who was allergic to all spirits except tequila but disliked drinking it straight. Cocktail historian David Wondrich notes the Margarita is related to the \"Daisy\" family of cocktails (margarita means \"daisy\" in Spanish), likely evolving from the Tequila Daisy. The first published recipe appeared in Esquire magazine in December 1953."},"named_after":{"en":"Named after Marjorie King, the customer for whom it was supposedly created, though \"margarita\" also means \"daisy\" in Spanish, linking it to the Daisy family of cocktails."}}'::jsonb, '{"profile":["sour","citrus","refreshing","balanced"],"description":{"en":"The Margarita delivers a vibrant, refreshing taste with bright lime acidity balanced by the sweet orange notes of triple sec and the earthy agave character of tequila. The optional salt rim enhances the citrus flavors while adding a savory contrast."},"first_impression":{"en":"Bright, zesty lime hits immediately, followed by sweet orange and agave warmth. The salt rim (if present) provides instant flavor complexity."},"finish":{"en":"Clean, crisp finish with lingering citrus and subtle agave sweetness. The tequila warmth develops gradually, leaving a refreshing aftertaste."},"balance":{"en":"Masterfully balanced between sour lime, sweet triple sec, and robust tequila. The proportions create perfect harmony where no single element dominates."}}'::jsonb, '{"best_time":["afternoon","evening","happy_hour"],"occasions":["party","beach","summer_gathering","casual"],"seasons":["summer","spring","all_year"],"food_pairings":{"en":"Perfect with Mexican cuisine: tacos, guacamole, ceviche, chips and salsa. Also excellent with grilled seafood, spicy dishes, and fresh citrus-based salads."},"ideal_for":{"en":"Perfect for casual gatherings, summer parties, beach days, and anyone who loves refreshing, citrus-forward cocktails. The most ordered cocktail worldwide."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_BLANCO","quantity":{"amount":50,"unit":"ml"},"display_name":"Tequila 100% Agave","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":20,"unit":"ml"},"display_name":"Triple Sec","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Freshly Squeezed Lime Juice","notes":null}]'::jsonb, 'shake', '{"en":"Optional: prepare glass with half salt rim by rubbing lime wedge on half the rim and dipping in coarse salt. Add all ingredients into a shaker with ice. Shake vigorously for 10-15 seconds. Strain into the prepared chilled cocktail glass."}'::jsonb, 'Cocktail glass', '{"en":"Half salt rim (optional)"}'::jsonb, 'none', 'up', '{"ING_TEQUILA_BLANCO"}', '{"sour","citrus","refreshing","balanced"}', 22, 168, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring","all_year"}', '{"party","beach","casual"}', false, false, '{}', 'Always use fresh lime juice - never bottled. Use 100% agave tequila for best results. Salt rim is optional - ask customer preference. For a frozen margarita, blend ingredients with ice. Popular variations include strawberry, mango, and spicy jalapeño versions.', 'mid', 98, 'https://iba-world.com/margarita/', 'IBA Official Recipe. Historical information from Esquire (1953) and cocktail historians David Wondrich and Robert Simonson.', 1)
