-- 20. Martinez
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('74ea4ca4-6089-4a12-8cec-9baee7799089', 'martinez', 'martinez', '{"en":"Martinez"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","vintage","stirred"}', '{"en":"The Martinez is a classic gin-based cocktail that predates the Martini. Rich and complex, it combines Old Tom gin with sweet vermouth, maraschino liqueur, and orange bitters for a sophisticated, slightly sweet aperitivo."}'::jsonb, '{"created_year":"1880s","origin":{"city":"San Francisco / Martinez","bar":"Occidental Hotel / Martinez Saloon","country":"United States"},"creator":{"name":"Jerry Thomas / Richelieue","profession":"bartender"},"story":{"en":"The Martinez''s true origin is disputed. One story attributes it to legendary bartender Jerry Thomas at San Francisco''s Occidental Hotel in the 1860s, created for a gold miner heading to Martinez, California. Another credits a bartender named Richelieue at a saloon in Martinez. First published in O.H. Byron''s The Modern Bartender in 1884, the Martinez is widely recognized as the direct predecessor to the Martini."},"named_after":{"en":"Named after the city of Martinez, California, the destination of a gold miner who requested the drink, or possibly the location where it was first created."}}'::jsonb, '{"profile":["sweet","herbal","aromatic","complex"],"description":{"en":"The Martinez offers a rich, complex flavor profile with botanical gin notes balanced by sweet vermouth. The maraschino liqueur adds subtle cherry sweetness and almond undertones, while orange bitters provide aromatic complexity. Smoother and sweeter than a modern Martini."},"first_impression":{"en":"Rich botanical gin and sweet vermouth open with herbal complexity, immediately followed by hints of cherry and citrus."},"finish":{"en":"Long, warming finish with lingering bitter orange and sweet maraschino notes. The botanical elements slowly fade, leaving pleasant herbal sweetness."},"balance":{"en":"Beautifully balanced between sweet and bitter, with the Old Tom gin and sweet vermouth creating harmony while maraschino and orange bitters add depth without overwhelming."}}'::jsonb, '{"best_time":["evening","pre-dinner"],"occasions":["aperitivo","date_night","special_occasion"],"seasons":["all_year"],"food_pairings":{"en":"Pairs excellently with aged cheeses, cured meats, smoked salmon, oysters, and light appetizers. The sweet-bitter balance complements rich, savory foods."},"ideal_for":{"en":"Perfect for cocktail enthusiasts who appreciate classic recipes, those who enjoy complex gin cocktails, and anyone seeking a sophisticated alternative to the modern Martini."}}'::jsonb, '[{"ingredient_id":"ING_GIN_OLD_TOM","quantity":{"amount":45,"unit":"ml"},"display_name":"Old Tom Gin","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":45,"unit":"ml"},"display_name":"Sweet Red Vermouth","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":1,"unit":"bar_spoon"},"display_name":"Maraschino Luxardo","notes":null},{"ingredient_id":"ING_ORANGE_BITTERS","quantity":{"amount":2,"unit":"dash"},"display_name":"Orange Bitters","notes":null}]'::jsonb, 'stir', '{"en":"Pour all ingredients into a mixing glass with ice cubes. Stir well for about 30 seconds until properly chilled and diluted. Strain into a chilled cocktail glass. Express lemon zest over the drink and use as garnish."}'::jsonb, 'Cocktail glass', '{"en":"Lemon zest"}'::jsonb, 'none', 'up', '{"ING_GIN_OLD_TOM"}', '{"boozy","sweet","herbal","aromatic"}', 28, 185, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all_year"}', '{"aperitivo","date_night","special_occasion"}', false, false, '{}', 'Use quality Old Tom gin for authentic sweetness. Stir gently to maintain clarity. The drink should be served ice-cold. If Old Tom gin is unavailable, London Dry gin with a small amount of simple syrup can be substituted, though the result will differ from the traditional recipe.', 'mid', 70, 'https://iba-world.com/martinez/', 'IBA Official Recipe. Historical information from The Modern Bartender (O.H. Byron, 1884) and cocktail historians.', 1)
