-- 23. Monkey Gland
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('4d5e6f7a-8b9c-0d1e-2f3a-4b5c6d7e8f9a', 'monkey-gland', 'b1c2d3e4f5a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0', '{"en":"Monkey Gland"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","gin","fruity","prohibition-era","controversial-name"}', '{"en":"A bold Prohibition-era cocktail combining gin with orange juice, grenadine, and a hint of absinthe. Despite its controversial name, this Harry''s New York Bar classic delivers a fruity, complex flavor profile that has stood the test of time."}'::jsonb, '{"created_year":"1920","origin":{"city":"Paris","bar":"Harry''s New York Bar","country":"France"},"creator":{"name":"Harry MacElhone","profession":"bartender"},"story":{"en":"The Monkey Gland was created by legendary bartender Harry MacElhone at Harry''s New York Bar in Paris during the 1920s. The cocktail''s unusual name references a controversial medical procedure of the era performed by Russian-French surgeon Serge Voronoff, who claimed to restore youth and vitality by transplanting monkey glands into humans. The procedure became a sensation in 1920s Paris, and MacElhone named the drink after this bizarre practice, suggesting the cocktail itself had rejuvenating properties. Despite the strange name, the drink became hugely popular among American expatriates in Paris during Prohibition."},"named_after":{"en":"Named after the controversial \"monkey gland\" transplant procedure performed by Dr. Serge Voronoff in 1920s Paris, which claimed to restore youth and virility."}}'::jsonb, '{"profile":["fruity","citrus","herbal"],"description":{"en":"Bright and fruity with orange juice taking the lead, balanced by botanical gin and a touch of grenadine sweetness. The absinthe adds a subtle anise complexity that elevates the drink beyond a simple gin and orange combination. The result is refreshing, slightly mysterious, and more sophisticated than its playful appearance suggests."},"first_impression":{"en":"Fresh orange juice with herbal gin complexity"},"finish":{"en":"Subtle anise notes from absinthe with lingering citrus"},"balance":{"en":"Well-balanced between fruity sweetness and botanical complexity - playful yet sophisticated"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","party","conversation","vintage_theme"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with French cuisine, smoked salmon canapés, cheese boards, roasted duck, and citrus-based desserts. Pairs beautifully with Middle Eastern mezze and herb-forward dishes."},"ideal_for":{"en":"Perfect for adventurous gin lovers who appreciate cocktails with history and character. An excellent conversation starter due to its unusual name, ideal for vintage cocktail enthusiasts and those exploring classic Prohibition-era drinks."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":45,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":2,"unit":"dash"},"display_name":"Grenadine syrup","notes":null},{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":2,"unit":"dash"},"display_name":"Absinthe","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake well until properly chilled. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Orange twist (optional)"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"fruity","citrus","herbal"}', 18, 150, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","party","conversation"}', false, false, '{"monkey-gland-royale","modern-monkey"}', 'Use fresh orange juice for best results. Absinthe should be used sparingly - just a hint to add complexity. Can substitute with pastis if absinthe unavailable. The name often requires explanation to curious guests - be prepared with the historical story.', 'mid', 52, 'https://iba-world.com/monkey-gland/', 'IBA Official Recipe. Historical information from Harry MacElhone''s "Harry''s ABC of Mixing Cocktails" and cocktail history sources.', 1)
