-- 27. Planter's Punch
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9e0f1a2b-3c4d-5e6f-7a8b-9c0d1e2f3a4b', 'planters-punch', 'd4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3', '{"en":"Planter''s Punch"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","rum","tropical","tiki","punch","refreshing"}', '{"en":"A legendary Caribbean rum punch that epitomizes tropical refreshment. This classic combines dark rum with fresh citrus juices and sugar syrup, creating a perfectly balanced drink that''s been quenching thirsts since the 19th century. Following the traditional \"one of sour, two of sweet, three of strong, four of weak\" formula."}'::jsonb, '{"created_year":"1878","origin":{"city":"Kingston","bar":"Myers Rum Distillery","country":"Jamaica"},"creator":{"name":"Fred L. Myers","profession":"distiller"},"story":{"en":"The Planter''s Punch dates back to 1878 when it first appeared in print in the Jamaican newspaper \"Fun.\" The recipe followed a simple rhyme: \"One of sour, two of sweet, three of strong, four of weak\" - referring to lime juice, sugar syrup, rum, and water. The drink was popularized by Fred L. Myers of the Myers Rum Distillery in Jamaica and became a signature offering at plantation parties. It spread throughout the Caribbean and became a staple of tiki culture in the mid-20th century. The name \"Planter''s\" refers to the colonial plantation owners who would have served this refreshing drink during hot Caribbean days."},"named_after":{"en":"Named after the plantation owners (planters) in the Caribbean who popularized this refreshing rum punch during the colonial era."}}'::jsonb, '{"profile":["tropical","citrus","sweet"],"description":{"en":"Bold and tropical with rich dark rum as the foundation, balanced by tart lime and orange juices. The sugar syrup rounds out the flavors, creating a harmonious blend that''s both refreshing and satisfying. The dark rum provides depth and complexity with notes of molasses and caramel, while the citrus keeps it bright and lively."},"first_impression":{"en":"Bright citrus with dark rum warmth and sweetness"},"finish":{"en":"Long, warming finish with lingering rum spices and tropical fruit notes"},"balance":{"en":"Perfectly balanced following the classic punch formula - sweet without being cloying, strong without being harsh"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["party","celebration","poolside","beach","tiki_night"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with Caribbean cuisine, jerk chicken, grilled seafood, tropical fruits, coconut-based dishes, and spicy island fare. Pairs beautifully with BBQ ribs and plantain chips."},"ideal_for":{"en":"Perfect for rum enthusiasts who love tropical classics. An excellent choice for summer parties, tiki gatherings, or anyone seeking an authentic Caribbean experience. The drink is social and shareable, making it ideal for group celebrations."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":35,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":35,"unit":"ml"},"display_name":"Fresh pineapple juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":10,"unit":"ml"},"display_name":"Grenadine syrup","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":3,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake well until properly chilled. Pour unstrained into a large glass filled with ice (can also strain if preferred)."}'::jsonb, 'Highball glass', '{"en":"Pineapple slice, maraschino cherry, and orange slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_DARK"}', '{"tropical","citrus","sweet"}', 12, 210, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","celebration","poolside","beach"}', false, false, '{"jamaican-planters-punch","navy-grog","tropical-punch"}', 'Use fresh juices for best results - never use concentrate. The classic ratio is "one of sour, two of sweet, three of strong, four of weak." Myers''s Dark Rum is traditional. Garnish generously for tropical presentation. Can be scaled up for party punch bowls.', 'mid', 70, 'https://iba-world.com/planters-punch/', 'IBA Official Recipe. Historical information from the Jamaican newspaper "Fun" (1878) and Myers Rum Distillery archives.', 1)
