-- 30. Remember the Maine
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a1b2c3d4-e5f6-4a7b-8c9d-0e1f2a3b4c5d', 'remember-the-maine', 'f8e7d6c5b4a3928170695847362514039281fade', '{"en":"Remember the Maine"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","stirred","strong","complex"}', '{"en":"A richly complex, Prohibition-era classic that blends the bold character of rye whiskey with the herbal depth of sweet vermouth, a touch of cherry liqueur, and a whisper of absinthe. Named after the rallying cry that sparked the Spanish-American War."}'::jsonb, '{"created_year":"1933","origin":{"city":"Havana","bar":"Unknown","country":"Cuba"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"Charles H. Baker Jr., in his book \"The Gentleman''s Companion\" (1939), states that he encountered this cocktail in Havana, Cuba during the Cuban Revolution of 1933. The cocktail is named after the press slogan \"Remember the Maine, to Hell with Spain!\" which blamed Spain for the unexplained sinking of the U.S.S. Maine off Cuba in 1898, helping provoke the Spanish-American War."},"named_after":{"en":"Named after the U.S.S. Maine battleship and the rallying cry \"Remember the Maine, to Hell with Spain!\" which became a slogan during the Spanish-American War."}}'::jsonb, '{"profile":["complex","herbal","boozy"],"description":{"en":"Complex and sophisticated with spicy rye whiskey at the forefront, balanced by sweet vermouth and enhanced with cherry notes from the liqueur. The absinthe rinse adds an elegant herbal whisper without overpowering the drink."},"first_impression":{"en":"Anise and herbal notes from absinthe, followed by bold rye spice"},"finish":{"en":"Long, warming finish with lingering cherry sweetness and herbal complexity"},"balance":{"en":"Beautifully balanced between strong spirit and aromatic modifiers"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["aperitivo","date_night","contemplative","nightcap"],"seasons":["autumn","winter","spring"],"food_pairings":{"en":"Excellent with charcuterie, aged cheeses, dark chocolate, or smoked meats. Also pairs well with rich desserts like chocolate cake or bread pudding."},"ideal_for":{"en":"Perfect for whiskey enthusiasts who appreciate complex, spirit-forward cocktails with historical significance. Ideal for those who enjoy Manhattan variations and bold, aromatic drinks."}}'::jsonb, '[{"ingredient_id":"ING_RYE_WHISKEY","quantity":{"amount":60,"unit":"ml"},"display_name":"Rye whiskey","notes":null},{"ingredient_id":"ING_SWEET_VERMOUTH","quantity":{"amount":20,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_CHERRY_HEERING","quantity":{"amount":10,"unit":"ml"},"display_name":"Cherry Heering","notes":null},{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":2,"unit":"barspoon"},"display_name":"Absinthe","notes":null}]'::jsonb, 'stir', '{"en":"Pour the absinthe into a coupe glass and swirl to completely coat the inside. Discard the absinthe and set the glass aside. Add the other ingredients to a mixing glass and fill it 3/4 full with ice. Stir until chilled, then strain into the glass rinsed with the absinthe."}'::jsonb, 'Coupe glass', '{"en":"None (or lemon twist optional)"}'::jsonb, 'none', 'up', '{"ING_RYE_WHISKEY"}', '{"complex","herbal","boozy"}', 30, 180, 'intermediate', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring"}', '{"aperitivo","date_night","contemplative","nightcap"}', false, false, '{"manhattan","brooklyn","vieux-carre"}', 'The absinthe rinse is essential - don''t skip it. Use Cherry Heering for authenticity. Can substitute bourbon for rye if needed, but rye''s spice is traditional. Ensure proper stirring to achieve ideal dilution.', 'mid', 55, 'https://iba-world.com/iba-cocktail/remember-the-maine/', 'IBA Official Recipe. Historical information from Charles H. Baker Jr. "The Gentleman''s Companion" (1939).', 1)
