-- 31. Rusty Nail
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f2a3b4c5-d6e7-4f8a-9b0c-1d2e3f4a5b6c', 'rusty-nail', 'b6c5d4e3f2a1b0c9d8e7f6a5b4c3d2e1f0a9b8c7', '{"en":"Rusty Nail"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","two-ingredient","stirred","after-dinner","scottish"}', '{"en":"A classic Scottish two-ingredient cocktail combining Scotch whisky with Drambuie, the honey and herb liqueur. Simple yet sophisticated, the Rusty Nail is warming, smooth, and perfect for sipping slowly. The golden color and rich flavor make it an ideal after-dinner drink."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Unknown","bar":"21 Club (popularized)","country":"USA/Scotland"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"While the exact origins are unclear, the Rusty Nail gained popularity in the 1960s and 1970s, particularly after being championed by the Rat Pack (Frank Sinatra, Dean Martin, and their crew) at New York''s 21 Club. The drink showcases Drambuie, a Scotch-based liqueur flavored with honey, herbs, and spices, which was created from a secret recipe given to the MacKinnon family by Bonnie Prince Charlie in 1746. The name \"Rusty Nail\" likely refers to the drink''s rusty golden color."},"named_after":{"en":"Named for the rusty golden color of the drink."}}'::jsonb, '{"profile":["sweet","herbal","warming"],"description":{"en":"Rich and warming with smoky Scotch balanced by sweet, honeyed Drambuie. The honey and herbal notes from Drambuie soften the whisky''s edges while maintaining complexity. Smooth and satisfying with a long, warming finish."},"first_impression":{"en":"Sweet honey and herbs followed by smoky Scotch"},"finish":{"en":"Long, warming finish with lingering honey, herbs, and smoke"},"balance":{"en":"Well-balanced between sweet Drambuie and smoky Scotch"}}'::jsonb, '{"best_time":["after_dinner","evening","nightcap"],"occasions":["digestivo","nightcap","contemplative","fireside"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect as a digestif with desserts, especially those with caramel, honey, or nuts. Pairs well with Scottish shortbread, chocolate, aged cheese, or smoked meats."},"ideal_for":{"en":"Perfect for Scotch enthusiasts who enjoy sweeter, more approachable whisky drinks. Ideal for those seeking a warming, simple yet sophisticated after-dinner cocktail."}}'::jsonb, '[{"ingredient_id":"ING_SCOTCH_WHISKY","quantity":{"amount":45,"unit":"ml"},"display_name":"Scotch whisky","notes":null},{"ingredient_id":"ING_DRAMBUIE","quantity":{"amount":25,"unit":"ml"},"display_name":"Drambuie","notes":null}]'::jsonb, 'build', '{"en":"Pour all ingredients directly into an old fashioned glass filled with ice. Stir gently. Garnish with lemon zest."}'::jsonb, 'Old Fashioned glass', '{"en":"Lemon zest"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_SCOTCH_WHISKY"}', '{"sweet","herbal","warming"}', 32, 180, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"digestivo","nightcap","contemplative"}', false, false, '{"rob-roy","bobby-burns","godfather"}', 'The official IBA ratio is 9 parts Scotch to 5 parts Drambuie (45ml:25ml). Can be adjusted to taste - some prefer equal parts. Build directly in glass for simplicity. Drambuie is essential - no substitutes. Stir gently to avoid over-dilution.', 'mid', 65, 'https://iba-world.com/iba-cocktail/rusty-nail/', 'IBA Official Recipe. Historical information from Drambuie history and 1960s cocktail culture.', 1)
