-- 38. Whiskey Sour
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e8fe6388-61dc-4290-a540-e48b6c271166', 'whiskey-sour', 'whiskey_sour_iba_unforgettables', '{"en":"Whiskey Sour"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","sour"}', '{"en":"A timeless classic balancing bourbon whiskey with fresh lemon juice and sugar syrup, optionally topped with a silky egg white foam. One of the most beloved sour cocktails in the world."}'::jsonb, '{"created_year":"1862","origin":{"city":"New York","bar":"Unknown","country":"United States"},"creator":{"name":"Sailors / Jerry Thomas","profession":"bartender"},"story":{"en":"The Whiskey Sour has roots dating back to sailors in the 1800s who added citrus to their whiskey to prevent scurvy during long voyages. The first written mention appeared in 1862 in Jerry Thomas''s \"Bartenders Guide.\" The Wisconsin newspaper Waukesha Plain Dealer first referenced the \"Whisky Sour\" in 1870. In 1872, former ship steward Elliot Staub is credited with \"inventing\" the drink in a bar in Iquique, then part of Peru. The addition of egg white became popular in the 1920s, creating the signature silky foam. Today, the Whiskey Sour is celebrated annually on August 25th and remains one of the most enduring classic cocktails."},"named_after":{"en":"Named after the \"sour\" category of cocktails, which combine spirits with citrus juice and sugar."}}'::jsonb, '{"profile":["sour","sweet","smooth"],"description":{"en":"A perfect harmony of sweet and sour, with the robust character of bourbon whiskey shining through bright lemon citrus and balanced sweetness. The optional egg white adds a luxurious, velvety texture and mellows the acidity. Each sip delivers warmth from the bourbon followed by refreshing citrus tang."},"first_impression":{"en":"The first sip greets you with a bright citrus punch tempered by sweetness, followed immediately by the warm, oaky notes of bourbon."},"finish":{"en":"A clean, slightly dry finish with lingering bourbon warmth and a subtle citrus echo. The egg white version leaves a soft, creamy aftertaste."},"balance":{"en":"Impeccably balanced between sweet and sour, with the bourbon providing a solid foundation. The ratio of 45:25:20 creates a drink that is neither too tart nor too sweet."}}'::jsonb, '{"best_time":["evening","afternoon"],"occasions":["aperitivo","casual_gathering","date_night"],"seasons":["all_year"],"food_pairings":{"en":"Pairs excellently with smoked meats, barbecue ribs, aged cheeses, roasted nuts, dark chocolate desserts, and charcuterie boards. The citrus cuts through rich, fatty foods beautifully."},"ideal_for":{"en":"Perfect for bourbon lovers seeking a refreshing citrus twist, and an ideal introduction to whiskey cocktails for those who find straight whiskey too strong. Great as a pre-dinner drink or evening sipper."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":45,"unit":"ml"},"display_name":"Bourbon Whiskey","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh Lemon Juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":20,"unit":"ml"},"display_name":"Sugar Syrup","notes":null},{"ingredient_id":"ING_EGG_WHITE","quantity":{"amount":1,"unit":"dash"},"display_name":"Egg White (Optional)","optional":true,"notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake well (shake harder if using egg white to release and incorporate the foam). Strain into a chilled cobbler glass, or for an on-the-rocks version, strain into an old fashioned glass with ice."}'::jsonb, 'Cobbler glass', '{"en":"Half orange slice and maraschino cherry, with optional orange zest"}'::jsonb, 'cubed', 'up', '{"ING_BOURBON"}', '{"sour","sweet","citrus","smooth"}', 18, 160, 'easy', 90, '{"sulphites","egg"}', '{"alcohol","sulphites_intolerance","egg_intolerance"}', '{"pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"gluten-free","dairy-free"}', '{"all_year"}', '{"aperitivo","casual_gathering","date_night"}', false, false, '{"New York Sour (with red wine float)","Boston Sour (with egg white)","Ward Eight (with grenadine and orange)"}', 'When making with egg white, dry shake first (without ice) for 10 seconds to emulsify, then add ice and shake again vigorously for 15-20 seconds. The IBA recipe calls for "few drops" of egg white, but modern bartenders typically use one full egg white for better foam. Always use fresh lemon juice - never bottled. Can be served either up in a cobbler glass or on the rocks in an old fashioned glass per customer preference.', 'mid', 95, 'https://iba-world.com/whiskey-sour/', 'IBA Official Recipe. Historical research from Jerry Thomas''s 1862 Bartenders Guide, Waukesha Plain Dealer 1870, and modern cocktail historians.', 1)
