-- 39. White Lady
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e5f6a7b8-c9d0-4e1f-2a3b-4c5d6e7f8a9b', 'white-lady', 'a9b8c7d6e5f4a3b2c1d0e9f8a7b6c5d4e3f2a1b0', '{"en":"White Lady"}'::jsonb, 'iba_official', 'Unforgettables', '{"iba","official","classic","sour","citrus","elegant"}', '{"en":"An elegant gin sour featuring the perfect trinity of gin, Cointreau, and fresh lemon juice. The White Lady is crisp, balanced, and sophisticated - a true symbol of classic cocktail refinement that has remained popular for nearly a century."}'::jsonb, '{"created_year":"1929","origin":{"city":"London","bar":"Harry''s New York Bar","country":"UK"},"creator":{"name":"Harry MacElhone","profession":"bartender"},"story":{"en":"First created by Harry MacElhone in 1919, the White Lady underwent a transformation in 1929 when gin replaced crème de menthe, defining the recipe we know today. Originally served at Ciro''s Club in London, this cocktail has traveled through decades, becoming a symbol of sophistication in bars worldwide. Some variations add egg white for extra silkiness, though the IBA standard recipe does not include it."},"named_after":{"en":"Named for its pale, elegant appearance and refined character."}}'::jsonb, '{"profile":["citrus","sharp","refreshing"],"description":{"en":"Crisp and bracingly tart with bright lemon leading the way, balanced by the orange sweetness of Cointreau and the botanical complexity of gin. Clean, refreshing, and perfectly balanced."},"first_impression":{"en":"Bright, sharp lemon with orange sweetness"},"finish":{"en":"Clean, crisp finish with lingering citrus and botanical notes"},"balance":{"en":"Perfect balance between tart lemon, sweet orange, and botanical gin"}}'::jsonb, '{"best_time":["aperitivo","evening","pre_dinner"],"occasions":["aperitivo","date_night","celebration","sophisticated_gathering"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with oysters, smoked salmon, light seafood, or as an aperitif before dinner. Pairs beautifully with citrus-based dishes and fresh salads."},"ideal_for":{"en":"Perfect for gin enthusiasts who appreciate clean, citrus-forward cocktails. Ideal for those who enjoy Margaritas or Daiquiris but prefer gin as their base spirit."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":40,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":30,"unit":"ml"},"display_name":"Triple sec (Cointreau)","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into cocktail shaker, shake well with ice, strain into chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Lemon twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","sharp","refreshing"}', 22, 180, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","celebration"}', false, false, '{"white-lady-with-egg-white","clover-club","aviation"}', 'Use Cointreau for best results - quality triple sec is essential. Fresh lemon juice only. Some guests may request egg white for silkiness - add 15ml if requested. Shake hard for proper dilution and chill.', 'mid', 75, 'https://iba-world.com/iba-cocktail/white-lady/', 'IBA Official Recipe. Historical information from Harry MacElhone and cocktail history sources.', 1)
