-- 41. Bellini
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b4c5d6e7-8f9a-0b1c-2d3e-4f5a6b7c8d9e', 'bellini', 'bellini_iba_contemporary_classic', '{"en":"Bellini"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","italian","brunch","elegant","prosecco","peach"}', '{"en":"Venice''s signature cocktail - an elegant blend of white peach purée and Prosecco. Light, refreshing, and quintessentially Italian, the Bellini is the perfect celebration of summer''s finest peaches."}'::jsonb, '{"created_year":"1948","origin":{"city":"Venice","bar":"Harry''s Bar","country":"Italy"},"creator":{"name":"Giuseppe Cipriani","profession":"bartender"},"story":{"en":"Created by Giuseppe Cipriani, founder of the legendary Harry''s Bar in Venice, between 1934-1948. The drink was named after the 15th-century Venetian artist Giovanni Bellini, as the pink color of the drink reminded Cipriani of a toga in one of Bellini''s paintings. It became the signature drink of Harry''s Bar and spread worldwide."},"named_after":{"en":"Named after the 15th-century Venetian painter Giovanni Bellini, inspired by the pink color of his paintings."}}'::jsonb, '{"profile":["fruity","delicate","sparkling"],"description":{"en":"Delicate, fruity, and effervescent. Fresh white peach purée provides natural sweetness and aroma, while Prosecco adds elegant bubbles and crisp dryness. Light, sophisticated, and incredibly refreshing."},"first_impression":{"en":"Delicate white peach with elegant Prosecco bubbles"},"finish":{"en":"Clean, refreshing finish with lingering peach notes"},"balance":{"en":"Perfectly balanced between peach sweetness and Prosecco dryness"}}'::jsonb, '{"best_time":["morning","afternoon"],"occasions":["brunch","celebration","wedding","summer_gathering"],"seasons":["summer"],"food_pairings":{"en":"Perfect with brunch dishes, Italian appetizers, prosciutto, fresh fruits, and light pastries. Ideal for summer celebrations."},"ideal_for":{"en":"Perfect for brunch lovers and those who appreciate elegant, light cocktails. Ideal for Prosecco enthusiasts and Italian cuisine lovers."}}'::jsonb, '[{"ingredient_id":"ING_PROSECCO","quantity":{"amount":100,"unit":"ml"},"display_name":"Prosecco","notes":null},{"ingredient_id":"ING_WHITE_PEACH_PUREE","quantity":{"amount":50,"unit":"ml"},"display_name":"White peach purée","notes":null}]'::jsonb, 'build', '{"en":"Pour peach purée into a chilled champagne flute. Gently top with chilled Prosecco. Stir gently and briefly. No garnish required, or optionally add a peach slice."}'::jsonb, 'Champagne flute', '{"en":"None (or optional peach slice)"}'::jsonb, 'none', 'up', '{"ING_PROSECCO"}', '{"fruity","delicate","sparkling"}', 7, 120, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer"}', '{"brunch","celebration","wedding","summer_gathering"}', false, false, '{"rossini","puccini","tintoretto"}', 'Traditionally made with white peaches (pesca bianca), which are in season in summer. Fresh peach purée is essential - avoid commercial peach nectar. The Rossini uses strawberry purée, Puccini uses tangerine juice. Prosecco should be well-chilled. Build gently to preserve bubbles.', 'mid', 85, 'https://iba-world.com/bellini/', 'IBA Official Recipe. Created at Harry''s Bar, Venice.', 1)
