-- 44. Caipirinha
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b8c9d0e1-f2a3-4b4c-5d6e-7f8a9b0c1d2e', 'caipirinha', 'caipirinha_iba_contemporary_2025', '{"en":"Caipirinha"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","brazilian","refreshing","summer","simple"}', '{"en":"Brazil''s national cocktail, made with cachaça, lime, and sugar. Refreshingly simple yet complex, the Caipirinha showcases cachaça''s grassy, vegetal character. A perfect beach drink that embodies Brazilian spirit."}'::jsonb, '{"created_year":"1918","origin":{"city":"São Paulo","country":"Brazil"},"story":{"en":"The Caipirinha originated in the state of São Paulo in the early 20th century, possibly as a medicinal remedy during the Spanish flu pandemic of 1918. Originally made with garlic, honey, and lime to cure illness, it evolved into the refreshing cocktail we know today. The name comes from \"caipira,\" meaning countryside person or hillbilly in Portuguese. It became Brazil''s national cocktail and gained worldwide popularity in the 1990s."},"named_after":{"en":"Named after \"caipira,\" a Portuguese word for countryside person or rural dweller."}}'::jsonb, '{"profile":["citrus","sweet","refreshing","grassy"],"description":{"en":"Fresh, bright, and incredibly refreshing. Muddled lime releases essential oils and juice, cachaça provides grassy, vegetal notes with slight funk, and sugar balances the acidity. Simple ingredients create complex flavor."},"first_impression":{"en":"Bright lime with grassy cachaça notes"},"finish":{"en":"Clean, refreshing finish with lingering citrus and sugar"},"balance":{"en":"Perfectly balanced between tart lime and sweet sugar"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","party","casual","celebration"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Brazilian churrasco, feijoada, grilled meats, seafood, ceviche, and spicy foods. Excellent with beach snacks and BBQ."},"ideal_for":{"en":"Perfect for anyone who loves fresh, citrus-forward cocktails. Ideal for beach days and summer parties. Great introduction to cachaça."}}'::jsonb, '[{"ingredient_id":"ING_CACHACA","quantity":{"amount":60,"unit":"ml"},"display_name":"Cachaça","notes":null},{"ingredient_id":"ING_LIME","quantity":{"amount":1,"unit":"whole"},"display_name":"Lime","notes":null},{"ingredient_id":"ING_SUGAR","quantity":{"amount":2,"unit":"tsp"},"display_name":"White sugar","notes":null}]'::jsonb, 'muddle', '{"en":"Place lime wedges and sugar in an Old Fashioned glass. Muddle gently to release lime juice and oils. Fill glass with crushed ice. Add cachaça and stir well. Serve with a straw."}'::jsonb, 'Old Fashioned glass', '{"en":"Lime wedge"}'::jsonb, 'crushed', 'built', '{"ING_CACHACA"}', '{"citrus","sweet","refreshing","grassy"}', 18, 180, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","party","casual","celebration"}', false, false, '{"caipiroska","caipifruta","caipirissima"}', 'Muddle gently - don''t pulverize the lime or it becomes bitter. Use fresh limes only. Crushed ice is traditional and essential for proper dilution. Quality cachaça makes a huge difference.', 'low', 88, 'https://iba-world.com/caipirinha/', 'IBA Official Recipe.', 1)
