-- 47. Corpse Reviver #2
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a3b4c5d6-e7f8-4a9b-0c1d-2e3f4a5b6c7d', 'corpse-reviver-2', 'corpse_reviver_2_iba_contemporary_2025', '{"en":"Corpse Reviver #2"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","classic","brunch","complex","citrus"}', '{"en":"A sophisticated \"hair of the dog\" cocktail combining gin, Cointreau, Lillet Blanc, lemon juice, and a hint of absinthe. Complex, citrus-forward, and refreshing, this drink was designed to cure hangovers and \"revive corpses.\""}'::jsonb, '{"created_year":"1930","origin":{"city":"London","bar":"Savoy Hotel","country":"England"},"creator":{"name":"Harry Craddock","profession":"bartender"},"story":{"en":"Featured in Harry Craddock''s 1930 \"The Savoy Cocktail Book,\" the Corpse Reviver #2 is part of a family of \"corpse reviver\" cocktails meant to cure hangovers. Craddock warned: \"Four of these taken in swift succession will unrevive the corpse again.\" It fell into obscurity but was revived (ironically) during the 2000s cocktail renaissance and is now a modern classic."},"named_after":{"en":"Named for its supposed ability to \"revive corpses\" or cure hangovers. The \"#2\" distinguishes it from other Corpse Reviver variations."}}'::jsonb, '{"profile":["citrus","herbal","complex","refreshing"],"description":{"en":"Bright, complex, and thoroughly refreshing. Lemon provides tartness, Cointreau adds orange sweetness, Lillet brings floral notes, gin provides botanical complexity, and absinthe adds an enigmatic anise touch. Perfectly balanced."},"first_impression":{"en":"Bright citrus with herbal, anise undertones"},"finish":{"en":"Clean, crisp finish with lingering absinthe complexity"},"balance":{"en":"Perfectly balanced between sweet, sour, and herbal elements"}}'::jsonb, '{"best_time":["brunch","afternoon"],"occasions":["brunch","recovery","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect for brunch with eggs, smoked salmon, or light seafood. Excellent before lunch as an aperitif."},"ideal_for":{"en":"Perfect for brunch, morning-after recovery, or anyone who appreciates complex, citrus-forward cocktails. Great introduction to absinthe."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_COINTREAU","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Cointreau","notes":null},{"ingredient_id":"ING_LILLET_BLANC","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Lillet Blanc","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":1,"unit":"dash"},"display_name":"Absinthe","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into shaker with ice. Shake well. Strain into chilled cocktail glass. Garnish with orange twist or cherry."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Orange twist or cherry"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","herbal","complex","refreshing"}', 24, 160, 'medium', 90, '{"sulphites","wormwood"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","recovery","casual"}', false, false, '{"corpse-reviver-1","corpse-reviver-blue"}', 'Use absinthe VERY sparingly - a single dash only, or rinse glass and discard excess. Too much will overpower. Originally used Kina Lillet (no longer available) - Lillet Blanc is modern substitute. Historically a morning-after drink.', 'mid', 72, 'https://iba-world.com/corpse-reviver-2/', 'IBA Official Recipe. From The Savoy Cocktail Book 1930.', 1)
