-- 52. Garibaldi
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f6a7b8c9-d0e1-4f2a-3b4c-5d6e7f8a9b0c', 'garibaldi', 'garibaldi_iba_contemporary_2025', '{"en":"Garibaldi"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","italian","simple","refreshing","brunch"}', '{"en":"A stunningly simple Italian cocktail combining Campari with freshly squeezed orange juice. Named after Italian revolutionary Giuseppe Garibaldi, this bright orange drink is beloved for its bittersweet simplicity."}'::jsonb, '{"created_year":"1860","origin":{"country":"Italy"},"story":{"en":"The Garibaldi was created in honor of Giuseppe Garibaldi, the Italian revolutionary hero who unified Italy in the 1860s. The drink''s bright orange color represents the color of Garibaldi''s army uniforms. In Italy, it''s sometimes called \"Campari Orange.\" The cocktail experienced a renaissance when bartender Gary Regan championed it in the early 2000s, noting that fluffy, freshly-squeezed orange juice (not carton juice) is essential."},"named_after":{"en":"Named after Giuseppe Garibaldi (1807-1882), Italian general and nationalist who played a key role in Italian unification."}}'::jsonb, '{"profile":["bitter","citrus","sweet"],"description":{"en":"Bright, refreshing, and bittersweet. Fresh orange juice brings natural sweetness and acidity, while Campari provides distinctive bitter, herbal complexity. The fluffy texture from fresh juice makes it incredibly drinkable."},"first_impression":{"en":"Fresh orange sweetness with immediate Campari bitterness"},"finish":{"en":"Pleasant bitter finish balanced by orange sweetness"},"balance":{"en":"Perfectly balanced between sweet orange and bitter Campari"}}'::jsonb, '{"best_time":["brunch","aperitivo","afternoon"],"occasions":["brunch","aperitivo","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect for brunch with eggs, pastries, or Italian antipasti. Excellent aperitivo with olives, prosciutto, cheese, or bruschetta."},"ideal_for":{"en":"Perfect for those who enjoy bitter flavors and Italian aperitivo culture. Great introduction to Campari for newcomers. Ideal for lazy brunches."}}'::jsonb, '[{"ingredient_id":"ING_CAMPARI","quantity":{"amount":45,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Fresh orange juice","notes":null}]'::jsonb, 'build', '{"en":"Build in a highball glass filled with ice. Pour Campari, then top with freshly squeezed orange juice. Stir gently. Garnish with an orange slice."}'::jsonb, 'Highball glass', '{"en":"Orange slice"}'::jsonb, 'cubes', 'built', '{"ING_CAMPARI"}', '{"bitter","citrus","sweet"}', 8, 160, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","aperitivo","casual"}', false, false, '{"garibaldi-spumante","negroni-sbagliato"}', 'CRITICAL: Must use freshly squeezed orange juice - never carton juice. The fluffy, pulpy texture is what makes this drink special. Some bartenders use a hand juicer for extra fluffy texture. Build in glass, don''t shake.', 'low', 70, 'https://iba-world.com/garibaldi/', 'IBA Official Recipe.', 1)
