-- 53. Golden Dream
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d0e1f2a3-b4c5-4d6e-7f8a-9b0c1d2e3f4a', 'golden-dream', 'golden_dream_iba_contemporary_2025', '{"en":"Golden Dream"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","creamy","sweet","dessert","orange"}', '{"en":"A luxurious creamy cocktail combining Galliano, Cointreau, orange juice, and cream. This golden-hued drink is like a liquid dessert, with vanilla, orange, and cream creating a sweet, indulgent experience."}'::jsonb, '{"created_year":"1960","origin":{"country":"USA"},"creator":{"name":"Raimonds Yansons","profession":"bartender"},"story":{"en":"Created by bartender Raimonds Yansons in the 1960s, the Golden Dream was part of the era''s trend toward creamy, dessert-style cocktails. It showcases Galliano, the tall Italian herbal liqueur with distinctive vanilla notes. The drink enjoyed popularity in the 1960s-70s and experienced a revival when added to the IBA Contemporary Classics list."},"named_after":{"en":"Named for its golden color and dreamy, dessert-like quality."}}'::jsonb, '{"profile":["sweet","creamy","vanilla","orange"],"description":{"en":"Smooth, sweet, and creamy. Galliano provides herbal vanilla notes, Cointreau adds orange sweetness, fresh orange juice brings acidity, and cream creates silky texture. A dessert in a glass."},"first_impression":{"en":"Creamy vanilla and orange sweetness"},"finish":{"en":"Smooth, sweet finish with lingering vanilla and orange"},"balance":{"en":"Sweet and creamy, balanced by orange acidity"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["dessert","after_dinner","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect as a dessert replacement or with light sweets like biscotti, fruit tarts, or vanilla cake. Best enjoyed on its own as an indulgent treat."},"ideal_for":{"en":"Perfect for those who love creamy, dessert-style cocktails. Great for after-dinner drinks and special occasions. Not for those who prefer dry cocktails."}}'::jsonb, '[{"ingredient_id":"ING_GALLIANO","quantity":{"amount":20,"unit":"ml"},"display_name":"Galliano","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":20,"unit":"ml"},"display_name":"Triple sec (Cointreau)","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh cream","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into shaker filled with ice. Shake vigorously. Strain into a chilled cocktail glass. No garnish needed."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"None"}'::jsonb, 'none', 'up', '{"ING_GALLIANO","ING_TRIPLE_SEC"}', '{"sweet","creamy","vanilla","orange"}', 15, 220, 'easy', 60, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter"}', '{"dessert","after_dinner","celebration"}', false, false, '{"golden-cadillac"}', 'Shake hard to properly emulsify the cream. Use fresh cream for best texture. Galliano is essential - its vanilla-anise flavor is what makes this drink special. Serve very cold.', 'mid', 55, 'https://iba-world.com/golden-dream/', 'IBA Official Recipe.', 1)
