INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c5d6e7f8-a9b0-4c1d-2e3f-4a5b6c7d8e9f', 'grasshopper', 'grasshopper_iba_contemporary_2025', '{"en":"Grasshopper"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","creamy","dessert","minty","sweet"}', '{"en":"A creamy, minty dessert cocktail combining crème de menthe, crème de cacao, and fresh cream. Bright green in color and refreshingly sweet, this drink is like a liquid mint chocolate dessert."}'::jsonb, '{"created_year":"1918","origin":{"city":"New Orleans","bar":"Tujague''s","country":"USA"},"creator":{"name":"Philibert Guichet","profession":"bartender"},"story":{"en":"Created in 1918 by Philibert Guichet, owner of Tujague''s bar in New Orleans'' French Quarter, for a cocktail competition. The drink won second place and gained popularity in the 1950s-60s as after-dinner drinks became fashionable. Its bright green color and sweet, minty flavor made it particularly popular among women in the mid-20th century."},"named_after":{"en":"Named for its bright green color, resembling a grasshopper."}}'::jsonb, '{"profile":["minty","creamy","sweet","chocolate"],"description":{"en":"Sweet, creamy, and refreshingly minty. Crème de menthe provides cool mint flavor, crème de cacao adds chocolate richness, and cream creates silky texture. A liquid mint chocolate chip ice cream."},"first_impression":{"en":"Cool mint with creamy sweetness"},"finish":{"en":"Smooth, sweet finish with lingering mint and chocolate"},"balance":{"en":"Well-balanced between mint and chocolate, unified by cream"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["dessert","after_dinner","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect as a dessert replacement or with chocolate desserts, mint brownies, or ice cream. Best enjoyed on its own as an indulgent after-dinner treat."},"ideal_for":{"en":"Perfect for those who love sweet, dessert-style cocktails and mint chocolate flavors. Great for after-dinner drinks. Not for those who prefer dry cocktails."}}'::jsonb, '[{"ingredient_id":"ING_CREME_DE_MENTHE_GREEN","quantity":{"amount":30,"unit":"ml"},"display_name":"Crème de menthe (green)","notes":null},{"ingredient_id":"ING_CREME_DE_CACAO_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"Crème de cacao (white)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh cream","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into shaker filled with ice. Shake vigorously. Strain into chilled cocktail glass. No garnish needed."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"None (optional: mint sprig)"}'::jsonb, 'none', 'up', '{"ING_CREME_DE_MENTHE_GREEN","ING_CREME_DE_CACAO_WHITE"}', '{"minty","creamy","sweet","chocolate"}', 15, 260, 'easy', 60, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter"}', '{"dessert","after_dinner","celebration"}', false, false, '{"frozen-grasshopper","flying-grasshopper"}', 'Shake hard to properly emulsify the cream. Use green crème de menthe for authentic color. White crème de cacao keeps the drink bright green. Can be made frozen/blended for a different experience.', 'mid', 60, 'https://iba-world.com/grasshopper/', 'IBA Official Recipe.', 1)
