-- 55. Hemingway Special
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d6e7f8a9-b0c1-4d2e-3f4a-5b6c7d8e9f0a', 'hemingway-special', 'hemingway_special_iba_contemporary_2025', '{"en":"Hemingway Special"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","daiquiri","citrus","literary","classic"}', '{"en":"A sophisticated daiquiri variation created for Ernest Hemingway, combining white rum, lime juice, grapefruit juice, and maraschino liqueur. Dry, complex, and citrus-forward, this drink reflects Hemingway''s preference for less-sweet cocktails."}'::jsonb, '{"created_year":"1930","origin":{"city":"Havana","bar":"El Floridita","country":"Cuba"},"creator":{"name":"Constantino Ribalaigua","profession":"bartender"},"story":{"en":"Created by Constantino Ribalaigua at El Floridita in Havana for Ernest Hemingway in the 1930s. Hemingway, who was diabetic, requested a daiquiri without sugar. Ribalaigua compensated by doubling the rum and adding grapefruit juice and maraschino liqueur. Hemingway loved it and it became his signature drink. He reportedly drank up to 17 in a single sitting. Also known as \"Papa Doble\" (Papa was Hemingway''s nickname, Doble for double rum)."},"named_after":{"en":"Named after Ernest Hemingway, the famous American novelist who frequented El Floridita."}}'::jsonb, '{"profile":["citrus","dry","complex","refreshing"],"description":{"en":"Dry, complex, and intensely citrus-forward. Fresh lime and grapefruit provide tart brightness, maraschino adds subtle almond sweetness, and rum brings smooth alcoholic backbone. A sophisticated, grown-up daiquiri."},"first_impression":{"en":"Bright grapefruit and lime tartness with subtle almond notes"},"finish":{"en":"Clean, dry finish with lingering citrus and rum"},"balance":{"en":"Dry and balanced - less sweet than traditional daiquiri"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","literary_event","beach"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with Cuban cuisine, seafood, ceviche, grilled fish, or oysters. The citrus cuts through rich flavors beautifully."},"ideal_for":{"en":"Perfect for those who prefer dry, citrus-forward cocktails. Great for rum lovers and Hemingway fans. Not for those who like sweet drinks."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":60,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":40,"unit":"ml"},"display_name":"Grapefruit juice","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":15,"unit":"ml"},"display_name":"Maraschino liqueur","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into shaker with ice. Shake vigorously. Strain into chilled cocktail glass. Garnish with lime wheel."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Lime wheel"}'::jsonb, 'none', 'up', '{"ING_RUM_WHITE"}', '{"citrus","dry","complex","refreshing"}', 20, 160, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","literary_event","beach"}', false, false, '{"papa-doble","hemingway-daiquiri"}', 'Also called "Papa Doble." Hemingway requested no sugar due to diabetes. Use fresh grapefruit juice for best results. Quality maraschino liqueur (like Luxardo) is essential. Can serve frozen/blended as Hemingway preferred.', 'mid', 70, 'https://iba-world.com/hemingway-special/', 'IBA Official Recipe. Created at El Floridita, Havana.', 1)
