INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d4e5f6a7-8b9c-0d1e-2f3a-4b5c6d7e8f9a', 'horses-neck', 'horses_neck_iba_contemporary_classic', '{"en":"Horse''s Neck"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","classic","simple","refreshing","highball"}', '{"en":"A classic highball combining brandy or bourbon with ginger ale, distinguished by its signature long spiral of lemon peel. Simple, refreshing, and elegant - a drink with old-world sophistication."}'::jsonb, '{"created_year":"1890","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Horse''s Neck dates back to the 1890s and was originally a non-alcoholic drink of ginger ale with lemon peel. Brandy was added later, creating the \"Horse''s Neck with a Kick.\" The distinctive spiral lemon peel garnish gives the drink its name, resembling a horse''s neck and mane."},"named_after":{"en":"Named after the long spiral lemon peel garnish that resembles a horse''s neck and mane."}}'::jsonb, '{"profile":["spicy","citrus","refreshing"],"description":{"en":"Refreshing and spicy from the ginger ale, with the warmth of brandy and bright citrus notes from the lemon peel. Light, bubbly, and easy-drinking with subtle complexity."},"first_impression":{"en":"Bright lemon and ginger spice with smooth brandy"},"finish":{"en":"Clean, refreshing finish with lingering ginger warmth"},"balance":{"en":"Well-balanced between spirit strength and ginger ale effervescence"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","relaxation","digestivo"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Pairs well with light appetizers, cheese plates, nuts, and casual fare. Excellent as a digestif after meals."},"ideal_for":{"en":"Perfect for those who prefer simple, refreshing cocktails with a touch of elegance. Great for brandy lovers seeking a lighter serve."}}'::jsonb, '[{"ingredient_id":"ING_BRANDY","quantity":{"amount":40,"unit":"ml"},"display_name":"Brandy (or Bourbon)","notes":null},{"ingredient_id":"ING_GINGER_ALE","quantity":{"amount":120,"unit":"ml"},"display_name":"Ginger ale","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Angostura bitters (optional)","notes":null}]'::jsonb, 'build', '{"en":"Place a long spiral of lemon peel into a highball glass. Fill with ice cubes. Pour in brandy and top with ginger ale. Stir gently. Optionally add a dash of Angostura bitters."}'::jsonb, 'Highball glass', '{"en":"Long spiral lemon peel"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_BRANDY"}', '{"spicy","citrus","refreshing"}', 8, 150, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"casual","relaxation","digestivo"}', false, false, '{"horses-neck-with-bourbon","virgin-horses-neck"}', 'The signature garnish is key - create a long spiral of lemon peel from top to bottom of the glass. Can be made with bourbon instead of brandy. The non-alcoholic version was the original.', 'low', 50, 'https://iba-world.com/horses-neck/', 'IBA Official Recipe.', 1)
