-- 60. Mai Tai
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e7f8a9b0-c1d2-4e3f-4a5b-6c7d8e9f0a1b', 'mai-tai', 'mai_tai_iba_contemporary_2025', '{"en":"Mai Tai"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","tiki","tropical","classic","rum"}', '{"en":"The quintessential tiki cocktail, combining rum, lime juice, orange curaçao, and orgeat syrup. Tropical, balanced, and complex, the Mai Tai is one of the world''s most beloved rum cocktails and a symbol of tiki culture."}'::jsonb, '{"created_year":"1944","origin":{"city":"Oakland","bar":"Trader Vic''s","country":"USA"},"creator":{"name":"Victor J. Bergeron (Trader Vic)","profession":"bartender"},"story":{"en":"Created in 1944 by Victor J. \"Trader Vic\" Bergeron at his restaurant in Oakland, California. When Vic served it to Tahitian friends, one exclaimed \"Maita''i roa ae!\" meaning \"Out of this world! The best!\" in Tahitian. The name stuck. The drink became a symbol of tiki culture and tropical escapism. Note: Don the Beachcomber also claimed to have created it in 1933, but Trader Vic''s version became the standard."},"named_after":{"en":"Tahitian phrase \"Maita''i roa ae\" meaning \"Out of this world!\" or \"The best!\""}}'::jsonb, '{"profile":["tropical","citrus","sweet","balanced"],"description":{"en":"Complex, balanced, and tropical. Fresh lime provides tartness, orgeat adds almond sweetness and richness, orange curaçao brings citrus depth, and rum provides the boozy backbone. A perfect tropical balance."},"first_impression":{"en":"Bright citrus with nutty almond sweetness"},"finish":{"en":"Smooth, tropical finish with lingering almond and rum"},"balance":{"en":"Perfectly balanced between sweet, sour, and nutty elements"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","party","celebration","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Polynesian cuisine, grilled seafood, teriyaki, coconut shrimp, tropical fruits, or tiki bar snacks."},"ideal_for":{"en":"Perfect for tiki enthusiasts, rum lovers, and anyone seeking tropical escapism. A must-have at beach bars and tiki establishments."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":40,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":20,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_ORANGE_CURACAO","quantity":{"amount":15,"unit":"ml"},"display_name":"Orange curaçao","notes":null},{"ingredient_id":"ING_ORGEAT","quantity":{"amount":15,"unit":"ml"},"display_name":"Orgeat syrup","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into shaker filled with ice. Shake vigorously. Strain into Old Fashioned glass filled with crushed ice. Garnish with mint sprig, pineapple wedge, and lime wheel."}'::jsonb, 'Old Fashioned glass (or Tiki mug)', '{"en":"Mint sprig, pineapple wedge, and lime wheel"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_WHITE","ING_RUM_DARK"}', '{"tropical","citrus","sweet","balanced"}', 20, 240, 'medium', 120, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance","tree_nut_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","party","celebration","casual"}', false, false, '{"royal-mai-tai","navy-grog"}', 'Use quality orgeat (almond syrup) - it''s essential. Original recipe used 17-year-old J. Wray & Nephew rum (no longer available). Crushed ice is traditional. Garnish lavishly for tiki presentation. Often served in special tiki mugs.', 'mid', 92, 'https://iba-world.com/mai-tai/', 'IBA Official Recipe. Original Trader Vic''s recipe 1944.', 1)
