-- 61. Margarita
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d4e5f6a7-b8c9-4d0e-1f2a-3b4c5d6e7f8a', 'margarita-contemporary', 'margarita_iba_contemporary_2025', '{"en":"Margarita"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","classic","tequila","citrus","salty"}', '{"en":"The world''s most popular tequila cocktail, combining tequila, lime juice, and orange liqueur, typically served with a salt rim. Perfectly balanced between sweet, sour, and salty, making it an endlessly refreshing classic."}'::jsonb, '{"created_year":"1938","origin":{"city":"Tijuana","bar":"Rancho La Gloria","country":"Mexico"},"creator":{"name":"Carlos \"Danny\" Herrera","profession":"bartender"},"story":{"en":"The Margarita''s origins are disputed, with several bartenders claiming its creation. The most accepted story credits Carlos \"Danny\" Herrera, who created it in 1938 at his Rancho La Gloria restaurant near Tijuana for customer Margarita Henkel, a showgirl who was allergic to all spirits except tequila. Other claims include bartender Francisco \"Pancho\" Morales in 1942. Regardless of its true origin, the Margarita became the most popular tequila cocktail worldwide."},"named_after":{"en":"Most likely named after Margarita Henkel, though some say it''s Spanish for \"daisy\" (the cocktail family it belongs to)."}}'::jsonb, '{"profile":["citrus","sweet","salty","tart"],"description":{"en":"Bright, tangy, and refreshing. The salt rim enhances the citrus flavors, tequila provides earthy agave notes, Cointreau adds orange sweetness, and fresh lime brings tartness. A perfect balance of all taste elements."},"first_impression":{"en":"Salty-citrus burst from the rim, followed by tart lime"},"finish":{"en":"Clean, crisp finish with lingering agave and citrus notes"},"balance":{"en":"Perfectly balanced trinity of sweet, sour, and salty"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","party","celebration","beach","bbq"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with Mexican cuisine, tacos, guacamole, ceviche, grilled fish, spicy foods, and chips with salsa. The acidity cuts through rich, fatty foods perfectly."},"ideal_for":{"en":"Perfect for tequila lovers and anyone who enjoys bright, citrus-forward cocktails. A crowd-pleasing favorite that works for any occasion."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA","quantity":{"amount":50,"unit":"ml"},"display_name":"Tequila 100% agave","notes":null},{"ingredient_id":"ING_COINTREAU","quantity":{"amount":20,"unit":"ml"},"display_name":"Cointreau","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SALT","quantity":{"amount":1,"unit":"pinch"},"display_name":"Salt","notes":null}]'::jsonb, 'shake', '{"en":"Rub the rim of a chilled cocktail glass with lime wedge and dip in salt. Pour all ingredients into shaker with ice cubes. Shake well. Strain into the prepared glass. Garnish with a lime wheel."}'::jsonb, 'Cocktail glass (Margarita)', '{"en":"Salt rim and lime wheel"}'::jsonb, 'none', 'up', '{"ING_TEQUILA"}', '{"citrus","sweet","salty","tart"}', 27, 170, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"casual","party","celebration","beach","bbq"}', false, false, '{"frozen-margarita","strawberry-margarita","spicy-margarita","cadillac-margarita"}', 'Use 100% agave tequila for best results. Cointreau is preferred over cheaper triple sec. Fresh lime juice is essential - never use bottled. Salt rim is optional - ask customer preference. Can serve on the rocks or frozen.', 'mid', 100, 'https://iba-world.com/margarita/', 'IBA Official Recipe.', 1)
