-- 63. Mint Julep
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d6e7f8a9-0b1c-2d3e-4f5a-6b7c8d9e0f1a', 'mint-julep-contemporary', 'mint_julep_contemporary_iba_classic', '{"en":"Mint Julep"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","bourbon","mint","southern","kentucky-derby","refreshing"}', '{"en":"The iconic bourbon cocktail of the American South, traditionally served in a frosted silver cup. Fresh mint, bourbon, and crushed ice create a refreshing masterpiece - the official drink of the Kentucky Derby."}'::jsonb, '{"created_year":"1780","origin":{"city":"Virginia / Kentucky","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"unknown"},"story":{"en":"The Mint Julep dates back to the late 1700s in the American South. It became the signature drink of the Kentucky Derby in 1938. Traditionally served in a silver or pewter cup that frosts when filled with crushed ice. The drink is deeply rooted in Southern hospitality and Kentucky bourbon culture."},"named_after":{"en":"The word \"julep\" comes from the Arabic \"julab,\" meaning rosewater - referring to sweet drinks."}}'::jsonb, '{"profile":["minty","sweet","boozy"],"description":{"en":"Refreshingly minty with robust bourbon warmth. Fresh mint provides cooling aromatics, sugar adds sweetness, and bourbon delivers oak and caramel notes. The crushed ice makes it incredibly refreshing."},"first_impression":{"en":"Fresh mint aromatics with sweet bourbon"},"finish":{"en":"Long, warming finish with lingering mint and oak"},"balance":{"en":"Well-balanced between mint freshness, sugar sweetness, and bourbon warmth"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["kentucky_derby","southern_gathering","outdoor","special_event"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Southern cuisine - fried chicken, barbecue, ham biscuits, and bourbon-infused desserts."},"ideal_for":{"en":"Perfect for bourbon lovers and anyone experiencing Southern hospitality. A must-have for Kentucky Derby parties and summer gatherings."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":60,"unit":"ml"},"display_name":"Bourbon","notes":null},{"ingredient_id":"ING_FRESH_MINT","quantity":{"amount":4,"unit":"sprigs"},"display_name":"Fresh mint sprigs","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_WATER","quantity":{"amount":10,"unit":"ml"},"display_name":"Water","notes":null}]'::jsonb, 'muddle', '{"en":"In a Julep cup or rocks glass, gently muddle the mint with simple syrup and water. Fill the glass with crushed ice and add bourbon. Stir until the cup frosts. Top with more crushed ice to form a dome. Garnish with a large mint bouquet and serve with a straw."}'::jsonb, 'Julep cup (silver) or rocks glass', '{"en":"Mint bouquet (large bunch of mint)"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_BOURBON"}', '{"minty","sweet","boozy"}', 25, 180, 'intermediate', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"kentucky_derby","southern_gathering","outdoor","special_event"}', false, false, '{"georgia-mint-julep","mojito","whiskey-smash"}', 'Traditionally served in a silver or pewter Julep cup. Muddle mint gently - don''t over-bruise. Use plenty of crushed ice and stir until cup frosts. The mint bouquet garnish should be large and aromatic. Serve with a short straw so the drinker''s nose is near the mint. Official drink of Kentucky Derby since 1938.', 'mid', 75, 'https://iba-world.com/mint-julep/', 'IBA Official Recipe. Official drink of the Kentucky Derby.', 1)
