-- 64. Mojito
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c3d4e5f6-a7b8-4c9d-0e1f-2a3b4c5d6e7f', 'mojito', 'mojito_iba_contemporary_2025', '{"en":"Mojito"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","refreshing","mint","summer","classic"}', '{"en":"Cuba''s most famous cocktail, combining white rum, fresh mint, lime juice, sugar, and soda water. Refreshingly herbaceous and perfectly balanced between sweet, sour, and minty freshness. A timeless summer classic."}'::jsonb, '{"created_year":"1586","origin":{"city":"Havana","bar":"La Bodeguita del Medio","country":"Cuba"},"story":{"en":"The Mojito has roots dating back to the 16th century with \"El Draque,\" a medicinal drink made by pirates with aguardiente, sugar, lime, and mint. The modern Mojito evolved in the 1800s when rum replaced aguardiente. It gained international fame at La Bodeguita del Medio in Havana, frequented by Ernest Hemingway who famously said \"My mojito in La Bodeguita, my daiquiri in El Floridita.\""},"named_after":{"en":"The name \"mojito\" likely comes from \"mojo,\" a Cuban seasoning, or from \"mojadito\" meaning \"a little wet\" in Spanish."}}'::jsonb, '{"profile":["refreshing","minty","citrus","sweet"],"description":{"en":"Bright, fresh, and incredibly refreshing. Fresh mint provides aromatic coolness, lime adds tartness, rum brings smoothness, and soda water creates effervescence. A perfectly balanced summer refresher."},"first_impression":{"en":"Burst of fresh mint and lime, cool and invigorating"},"finish":{"en":"Clean, refreshing finish with lingering mint and citrus"},"balance":{"en":"Perfectly balanced between sweet sugar, tart lime, and fresh mint"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","beach","party","bbq","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with Cuban cuisine, grilled seafood, ceviche, light salads, tropical fruits, and spicy foods. The mint and lime cut through rich flavors beautifully."},"ideal_for":{"en":"Perfect for anyone who loves fresh, herbaceous cocktails. Ideal for hot weather and outdoor gatherings. A universally loved crowd-pleaser."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":45,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_MINT","quantity":{"amount":6,"unit":"leaves"},"display_name":"Fresh mint leaves","notes":null},{"ingredient_id":"ING_SUGAR","quantity":{"amount":2,"unit":"tsp"},"display_name":"White sugar","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":1,"unit":"splash"},"display_name":"Soda water","notes":null}]'::jsonb, 'muddle', '{"en":"Muddle mint leaves with sugar and lime juice in a highball glass. Fill the glass with ice cubes. Add rum and top with soda water. Stir gently to combine. Garnish with mint sprig and lime wheel."}'::jsonb, 'Highball glass', '{"en":"Mint sprig and lime wheel"}'::jsonb, 'cubes', 'built', '{"ING_RUM_WHITE"}', '{"refreshing","minty","citrus","sweet"}', 10, 150, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","beach","party","bbq","brunch"}', false, false, '{"virgin-mojito","strawberry-mojito","blackberry-mojito","coconut-mojito"}', 'Gently muddle mint - don''t over-muddle or it becomes bitter. Use fresh mint only, never dried. Build in serving glass to preserve carbonation. Can use simple syrup instead of sugar for easier dissolution.', 'low', 98, 'https://iba-world.com/mojito/', 'IBA Official Recipe. Historical information from Cuban cocktail archives.', 1)
