-- 65. Moscow Mule
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f2a3b4c5-6d7e-8f9a-0b1c-2d3e4f5a6b7c', 'moscow-mule', 'moscow_mule_iba_contemporary_classic', '{"en":"Moscow Mule"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","refreshing","spicy","vodka","iconic","copper-mug"}', '{"en":"An iconic vodka cocktail served in a distinctive copper mug. Vodka, spicy ginger beer, and fresh lime create a refreshing, zingy drink that launched vodka''s popularity in America."}'::jsonb, '{"created_year":"1941","origin":{"city":"Los Angeles","bar":"Cock ''n'' Bull","country":"USA"},"creator":{"name":"John Martin & Jack Morgan","profession":"bartenders/businessmen"},"story":{"en":"Created around 1941 at the Cock ''n'' Bull pub in Los Angeles by John Martin (Smirnoff vodka distributor) and Jack Morgan (bar owner with excess ginger beer). They added lime and served it in copper mugs to create a marketing sensation. The drink single-handedly launched vodka''s popularity in America and created the copper mug tradition."},"named_after":{"en":"Named \"Moscow Mule\" - \"Moscow\" for vodka''s Russian origins and \"Mule\" for ginger beer''s kick."}}'::jsonb, '{"profile":["spicy","citrus","refreshing"],"description":{"en":"Crisp, refreshing, and spicy. Ginger beer provides warming spice and effervescence, lime adds bright citrus, and vodka keeps it clean. The copper mug keeps it ice-cold. Invigorating and thirst-quenching."},"first_impression":{"en":"Bright lime and spicy ginger with clean vodka"},"finish":{"en":"Clean, spicy finish with lingering ginger warmth"},"balance":{"en":"Well-balanced between ginger spice, lime tartness, and vodka smoothness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","outdoor","summer_gathering","brunch"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Pairs well with spicy foods, Asian cuisine, burgers, barbecue, and appetizers. The ginger complements bold flavors."},"ideal_for":{"en":"Perfect for anyone seeking a refreshing, easy-drinking cocktail. Great for vodka lovers and those who enjoy ginger''s spicy kick."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GINGER_BEER","quantity":{"amount":120,"unit":"ml"},"display_name":"Ginger beer","notes":null}]'::jsonb, 'build', '{"en":"Combine vodka and lime juice in a copper mug filled with ice. Top with ginger beer. Stir gently. Garnish with a lime wedge and fresh mint sprig."}'::jsonb, 'Copper mug (or highball glass)', '{"en":"Lime wedge and fresh mint sprig"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"spicy","citrus","refreshing"}', 10, 180, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"casual","outdoor","summer_gathering","brunch"}', false, false, '{"kentucky-mule","mexican-mule","london-mule"}', 'Traditionally served in a copper mug which keeps the drink ice-cold and adds to the experience. Use quality ginger beer (not ginger ale). Fresh lime juice is essential. The Kentucky Mule uses bourbon, Mexican Mule uses tequila.', 'low', 90, 'https://iba-world.com/moscow-mule/', 'IBA Official Recipe. The drink that made vodka popular in America.', 1)
