-- 72. Tequila Sunrise
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b8c9d0e1-2f3a-4b5c-6d7e-8f9a0b1c2d3e', 'tequila-sunrise', 'tequila_sunrise_iba_contemporary_classic', '{"en":"Tequila Sunrise"}'::jsonb, 'iba_official', 'Contemporary', '{"iba","official","fruity","visual","summer","iconic","easy"}', '{"en":"A visually stunning cocktail that lives up to its name - tequila and orange juice layered with grenadine to create a sunrise effect. Beautiful, fruity, and refreshing, it''s as pleasing to the eye as it is to the palate."}'::jsonb, '{"created_year":"1970","origin":{"city":"Sausalito","bar":"Trident Restaurant","country":"USA"},"creator":{"name":"Bobby Lozoff and Billy Rice","profession":"bartenders"},"story":{"en":"The modern Tequila Sunrise was created in the early 1970s by Bobby Lozoff and Billy Rice at the Trident Restaurant in Sausalito, California. It gained massive popularity when Mick Jagger tried it and ordered the drink everywhere during the Rolling Stones'' 1972 American tour, leading to it being called \"The Tequila Sunrise tour.\""},"named_after":{"en":"Named for the visual effect created by the grenadine settling through the drink, resembling a sunrise."}}'::jsonb, '{"profile":["fruity","sweet","citrus"],"description":{"en":"Bright and refreshing with prominent orange citrus flavor complemented by sweet grenadine. Tequila adds earthy agave notes and warmth. Light, easy-drinking, and crowd-pleasing."},"first_impression":{"en":"Sweet orange and cherry with smooth tequila"},"finish":{"en":"Clean, fruity finish with subtle agave warmth"},"balance":{"en":"Well-balanced between sweet grenadine and citrus orange"}}'::jsonb, '{"best_time":["morning","afternoon","evening"],"occasions":["brunch","beach","pool_party","casual"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Mexican cuisine, breakfast dishes, fresh fruit, grilled seafood, and light appetizers."},"ideal_for":{"en":"Ideal for anyone seeking a visually appealing, easy-drinking cocktail. Perfect for brunch, poolside, or introducing friends to tequila."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA","quantity":{"amount":45,"unit":"ml"},"display_name":"Tequila","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Grenadine syrup","notes":null}]'::jsonb, 'build', '{"en":"Pour tequila and orange juice directly into a highball glass filled with ice cubes. Add grenadine by carefully pouring it down the side of the glass - it will sink and gradually rise to create a sunrise effect. Do not stir. Garnish with an orange slice and cherry."}'::jsonb, 'Highball glass', '{"en":"Orange slice and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_TEQUILA"}', '{"fruity","sweet","citrus"}', 10, 200, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","beach","pool_party","casual"}', false, false, '{"tequila-sunset","vodka-sunrise","mezcal-sunrise"}', 'DO NOT STIR after adding grenadine - the layered sunrise effect is key to presentation. Pour grenadine slowly down the inside of the glass. Use fresh-squeezed orange juice for best results. Guest can stir before drinking.', 'low', 90, 'https://iba-world.com/tequila-sunrise/', 'IBA Official Recipe. Made famous by The Rolling Stones in the 1970s.', 1)
