VALUES ('4d5e6f7a-8b9c-0d1e-2f3a-4b5c6d7e8f9a', 'barracuda', 'd4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3', '{"en":"Barracuda"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","rum","tropical","sparkling","refreshing"}', '{"en":"A tropical sparkling cocktail combining gold rum with pineapple, lime, and Galliano, topped with sparkling wine. Refreshing and elegant with a perfect balance of tropical fruit and effervescence."}'::jsonb, '{"created_year":"1980","origin":{"city":"Unknown","bar":"Unknown","country":"Unknown"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Barracuda emerged in the 1980s as a tropical variation on the classic Champagne cocktail. Its combination of rum, pineapple, and sparkling wine made it popular in beach resorts and tropical destinations. The addition of Galliano adds a distinctive herbal-vanilla note that sets it apart from simpler tropical drinks."},"named_after":{"en":"Named after the barracuda fish, known for being swift and striking - qualities that match the drink''s refreshing and bold character."}}'::jsonb, '{"profile":["tropical","sparkling","fruity"],"description":{"en":"Tropical pineapple and lime juice balanced with rum warmth, herbal Galliano notes, and celebratory sparkling wine. Light, refreshing, and sophisticated."},"first_impression":{"en":"Bright tropical fruit with sparkling effervescence"},"finish":{"en":"Clean, crisp finish with lingering vanilla and herbal notes"},"balance":{"en":"Perfectly balanced between tropical sweetness, citrus tartness, and sparkling wine elegance"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["celebration","party","brunch","beach"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with tropical fruit platters, seafood dishes, grilled fish, or as a celebratory aperitif."},"ideal_for":{"en":"Perfect for those who enjoy tropical flavors with an elegant twist. Ideal for celebrations or anyone seeking a sophisticated beach cocktail."}}'::jsonb, '[{"ingredient_id":"ING_RUM_GOLD","quantity":{"amount":45,"unit":"ml"},"display_name":"Gold rum","notes":null},{"ingredient_id":"ING_GALLIANO","quantity":{"amount":15,"unit":"ml"},"display_name":"Galliano","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_PROSECCO","quantity":{"amount":60,"unit":"ml"},"display_name":"Prosecco","notes":null}]'::jsonb, 'shake', '{"en":"Shake rum, Galliano, pineapple juice, and lime juice with ice. Strain into a highball glass filled with ice. Top with Prosecco."}'::jsonb, 'Highball glass', '{"en":"Pineapple wedge and cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_GOLD"}', '{"tropical","sparkling","fruity"}', 12, 210, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"celebration","party","brunch"}', false, false, '{}', 'Add Prosecco last to preserve carbonation. Use fresh pineapple juice for best results. Can substitute Champagne for Prosecco.', 'mid', 65, 'https://iba-world.com/barracuda/', 'IBA Official Recipe', 1)
