-- 79. Canchánchara
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('6f7a8b9c-0d1e-2f3a-4b5c-6d7e8f9a0b1c', 'canchanchara', 'f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5', '{"en":"Canchánchara"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","rum","cuban","historical","simple"}', '{"en":"One of Cuba''s oldest cocktails, the Canchánchara is a simple yet historic blend of rum, honey, and lime. This rustic drink was the precursor to the Daiquiri and remains a beloved Cuban classic."}'::jsonb, '{"created_year":"1868","origin":{"city":"Trinidad","bar":"Unknown","country":"Cuba"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Canchánchara dates back to the Cuban War of Independence (1868-1878). Cuban independence fighters, known as \"mambises,\" would mix aguardiente (a raw rum-like spirit), honey, and lime juice as both refreshment and medicine. The drink is named after a traditional Cuban song and dance. It became popular in Trinidad, Cuba, where it''s still served in traditional clay cups called \"jícaras.\""},"named_after":{"en":"Named after a traditional Cuban song and dance from the independence era."}}'::jsonb, '{"profile":["sweet","citrus","simple"],"description":{"en":"Rustic and straightforward with natural honey sweetness, bright lime tartness, and the raw character of aguardiente or light rum. Simple yet perfectly balanced."},"first_impression":{"en":"Bright lime and honey sweetness with rum warmth"},"finish":{"en":"Clean, sweet finish with lingering honey and citrus notes"},"balance":{"en":"Perfectly balanced between honey sweetness and lime tartness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","historical_tasting","beach","party"],"seasons":["summer","spring"],"food_pairings":{"en":"Excellent with Cuban cuisine, grilled pork, plantains, or traditional Cuban sandwiches."},"ideal_for":{"en":"Perfect for rum enthusiasts interested in cocktail history. Ideal for those who appreciate simple, rustic drinks with authentic Cuban heritage."}}'::jsonb, '[{"ingredient_id":"ING_RUM_LIGHT","quantity":{"amount":60,"unit":"ml"},"display_name":"Light rum","notes":null},{"ingredient_id":"ING_HONEY","quantity":{"amount":20,"unit":"ml"},"display_name":"Honey","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_WATER","quantity":{"amount":30,"unit":"ml"},"display_name":"Water","notes":null}]'::jsonb, 'stir', '{"en":"Mix honey with water and lime juice in a traditional clay cup or rocks glass. Add rum and stir well. Add ice if desired."}'::jsonb, 'Rocks glass (or traditional clay cup)', '{"en":"Lime wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_LIGHT"}', '{"sweet","citrus","simple"}', 18, 150, 'easy', 30, '{}', '{"alcohol"}', '{"vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"summer","spring"}', '{"casual","beach","party"}', false, false, '{}', 'Traditionally served in clay cups called "jícaras." Use raw honey for authentic flavor. Can be served with or without ice.', 'low', 50, 'https://iba-world.com/canchanchara/', 'IBA Official Recipe', 1)
