-- 80. Chartreuse Swizzle
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f6a7b8c9-0d1e-2f3a-4b5c-6d7e8f9a0b1c', 'chartreuse-swizzle', 'd9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8', '{"en":"Chartreuse Swizzle"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","tiki","herbal","refreshing","swizzle"}', '{"en":"A modern tiki classic featuring the herbaceous complexity of Green Chartreuse swizzled with pineapple, lime, and falernum over crushed ice. This refreshing cocktail combines Caribbean swizzle tradition with French herbal liqueur sophistication."}'::jsonb, '{"created_year":"2004","origin":{"city":"San Francisco","bar":"Alembic","country":"USA"},"creator":{"name":"Marcovaldo Dionysos","profession":"bartender"},"story":{"en":"Created by Marcovaldo Dionysos at the Alembic bar in San Francisco around 2004. The drink was inspired by classic Caribbean swizzles but substitutes Green Chartreuse for rum, creating a more herbaceous and complex profile. The swizzle technique, using a traditional swizzle stick or bar spoon, aerates the drink and creates a frosty, diluted consistency perfect for sipping. The cocktail gained popularity during the tiki revival of the 2000s."},"named_after":{"en":"Named for the \"swizzle\" technique of rapidly rotating a stick or spoon in crushed ice to chill and dilute the drink, combined with the dominant Chartreuse flavor."}}'::jsonb, '{"profile":["herbal","tropical","complex","refreshing"],"description":{"en":"Intensely herbaceous with Green Chartreuse''s 130 botanical complexity balanced by tropical pineapple sweetness, tart lime, and aromatic falernum spices. The swizzle dilution makes it remarkably smooth and refreshing."},"first_impression":{"en":"Bold herbal Chartreuse with tropical pineapple and lime brightness"},"finish":{"en":"Long, complex finish with lingering herbal notes and tropical warmth"},"balance":{"en":"Masterfully balanced between intense herbal complexity and tropical fruit sweetness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["tiki_party","garden_party","special_occasion","summer_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs well with Caribbean jerk dishes, grilled seafood, Thai cuisine, tropical fruits, and herb-crusted meats."},"ideal_for":{"en":"Perfect for adventurous drinkers who love herbal complexity in tropical cocktails. Ideal for Chartreuse enthusiasts and those seeking a sophisticated tiki experience. Great for hot weather sipping."}}'::jsonb, '[{"ingredient_id":"ING_CHARTREUSE_GREEN","quantity":{"amount":45,"unit":"ml"},"display_name":"Green Chartreuse","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_FALERNUM","quantity":{"amount":15,"unit":"ml"},"display_name":"Falernum","notes":null}]'::jsonb, 'swizzle', '{"en":"Add all ingredients to a Collins or highball glass. Fill with crushed ice. Insert a swizzle stick or bar spoon and rapidly rotate between your palms for 10-15 seconds until the outside of the glass frosts. Top with more crushed ice to form a snow cone. Garnish with a pineapple frond and fresh mint sprig."}'::jsonb, 'Collins or Highball glass', '{"en":"Pineapple frond and fresh mint sprig"}'::jsonb, 'crushed', 'swizzle', '{"ING_CHARTREUSE_GREEN"}', '{"herbal","tropical","complex","refreshing"}', 14, 210, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","special_occasion"}', false, false, '{"yellow-chartreuse-swizzle","rum-chartreuse-swizzle"}', 'Green Chartreuse is essential (55% ABV). Proper swizzle technique creates frost on outside of glass. Use plenty of crushed ice. Falernum adds essential spice notes. Fresh pineapple juice preferred.', 'premium', 70, 'https://iba-world.com/chartreuse-swizzle/', 'IBA Official Recipe. Created by Marcovaldo Dionysos at Alembic, San Francisco.', 1)
