VALUES ('2b3c4d5e-6f7a-8b9c-0d1e-2f3a4b5c6d7e', 'espresso-martini', 'b2c3d4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1', '{"en":"Espresso Martini"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","coffee","vodka","modern-classic","energizing"}', '{"en":"A modern classic that perfectly combines rich espresso with smooth vodka and coffee liqueur. This energizing cocktail features a signature foam top created by the natural oils in coffee."}'::jsonb, '{"created_year":"1983","origin":{"city":"London","bar":"Soho Brasserie","country":"UK"},"creator":{"name":"Dick Bradsell","profession":"bartender"},"story":{"en":"Created by legendary bartender Dick Bradsell at the Soho Brasserie in London in 1983. According to legend, a famous model asked Dick to make her a drink that would \"wake me up and then f*** me up.\" The result was the Espresso Martini, originally called the Vodka Espresso. It became one of the most iconic cocktails of the modern era."},"named_after":{"en":"Originally called \"Vodka Espresso,\" later renamed \"Espresso Martini\" due to its martini glass presentation and coffee-forward profile."}}'::jsonb, '{"profile":["bitter","sweet","rich"],"description":{"en":"Rich coffee flavor balanced with vodka smoothness and coffee liqueur sweetness. Velvety texture with a pleasant bitterness and sweet finish."},"first_impression":{"en":"Bold espresso aroma and rich coffee flavor with subtle sweetness"},"finish":{"en":"Smooth, lingering coffee finish with a touch of sweetness"},"balance":{"en":"Perfect balance between coffee bitterness, liqueur sweetness, and vodka smoothness"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","nightcap","date_night","pre_dinner"],"seasons":["all_seasons"],"food_pairings":{"en":"Pairs wonderfully with chocolate desserts, tiramisu, or as an after-dinner pick-me-up. Also excellent with chocolate truffles or biscotti."},"ideal_for":{"en":"Perfect for coffee lovers who want an energizing cocktail. Ideal for those who enjoy rich, bold flavors and need a sophisticated pick-me-up."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":10,"unit":"ml"},"display_name":"Coffee liqueur","notes":null},{"ingredient_id":"ING_ESPRESSO","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh espresso","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake vigorously until well-chilled and a foam forms. Double strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Three coffee beans"}'::jsonb, 'none', 'up', '{"ING_VODKA"}', '{"bitter","sweet","rich"}', 17, 190, 'easy', 60, '{}', '{"alcohol","caffeine"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-season"}', '{"party","nightcap","date_night"}', false, false, '{"vodka-espresso","pharmaceutical-stimulant"}', 'Use freshly brewed espresso that has cooled slightly. Shake vigorously to create the signature foam. The three coffee beans traditionally represent health, wealth, and happiness.', 'mid', 95, 'https://iba-world.com/espresso-martini/', 'IBA Official Recipe', 1)
