-- 84. Fernandito
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b4c5d6e7-8f9a-0b1c-2d3e-4f5a6b7c8d9e', 'fernandito', 'f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4f5a6', '{"en":"Fernandito"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","italian","amaro","simple","digestivo","bitter"}', '{"en":"A simple yet sophisticated Italian digestivo combining Fernet-Branca with cola and ice. This minimal serve showcases the intense herbal bitterness of Fernet balanced by cola''s sweetness and effervescence, creating a refreshing and complex drink that''s beloved in Argentina and increasingly popular worldwide."}'::jsonb, '{"created_year":"1950s","origin":{"city":"Córdoba","bar":"Unknown","country":"Argentina"},"creator":{"name":"Unknown","profession":"unknown"},"story":{"en":"The Fernandito emerged in Argentina in the 1950s-60s, where Fernet-Branca became extraordinarily popular among Italian immigrants. The drink''s name is a diminutive of \"Fernando,\" possibly referring to an early drinker or bartender who popularized the serve. In Córdoba, Argentina, Fernet con cola became a cultural phenomenon, with Argentinians consuming more Fernet-Branca per capita than any other country, including Italy. The drink represents the successful fusion of Italian bitter culture with Argentine social drinking traditions."},"named_after":{"en":"\"Fernandito\" is the diminutive form of \"Fernando\" in Spanish, possibly named after an early patron or bartender who popularized the drink."}}'::jsonb, '{"profile":["bitter","herbal","sweet","refreshing"],"description":{"en":"Intensely bitter and herbal from Fernet-Branca''s complex blend of botanicals (saffron, myrrh, chamomile, etc.), balanced by cola''s caramel sweetness and effervescence. The combination is surprisingly harmonious, with neither element overpowering the other."},"first_impression":{"en":"Bold herbal bitterness with cola sweetness and mint-menthol coolness"},"finish":{"en":"Long, bitter finish with lingering herbal complexity and mint freshness"},"balance":{"en":"Well balanced between intense bitterness and cola sweetness - an acquired taste that rewards exploration"}}'::jsonb, '{"best_time":["evening","after_dinner","late_night"],"occasions":["digestivo","social_gathering","casual","party"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Excellent as a digestivo after heavy meals, with Argentine asado, grilled meats, empanadas, and rich pasta dishes."},"ideal_for":{"en":"Perfect for those who appreciate bitter digestivos and amaro culture. Ideal for adventurous drinkers seeking an authentic Argentine experience. Great for social drinking and after-dinner refreshment."}}'::jsonb, '[{"ingredient_id":"ING_FERNET_BRANCA","quantity":{"amount":50,"unit":"ml"},"display_name":"Fernet-Branca","notes":null},{"ingredient_id":"ING_COLA","quantity":{"amount":100,"unit":"ml"},"display_name":"Cola","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball or rocks glass with ice cubes. Add Fernet-Branca. Top with cola. Stir gently to combine. No garnish needed - the drink speaks for itself."}'::jsonb, 'Highball or Rocks glass', '{"en":"None (optional: lemon or lime wedge)"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_FERNET_BRANCA"}', '{"bitter","herbal","sweet","refreshing"}', 10, 150, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"digestivo","casual","social"}', false, false, '{"fernet-con-cola","fernando"}', 'Fernet-Branca is essential - do not substitute. Ratio can be adjusted to taste (Argentine style is typically 50/50). Serve very cold with plenty of ice. An acquired taste that many grow to love.', 'mid', 65, 'https://iba-world.com/fernandito/', 'IBA Official Recipe. Popular in Argentina since the 1950s-60s.', 1)
