-- 85. French Martini
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c5d6e7f8-9a0b-1c2d-3e4f-5a6b7c8d9e0f', 'french-martini', 'a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4f5a6b7', '{"en":"French Martini"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","vodka","fruity","elegant","1990s"}', '{"en":"A luxurious and fruity cocktail featuring vodka, Chambord raspberry liqueur, and pineapple juice. Despite its name, this elegant drink is neither French nor a true Martini, but rather a sophisticated vodka cocktail with a beautiful foam cap and complex fruit flavors that became iconic in the 1990s."}'::jsonb, '{"created_year":"1980s","origin":{"city":"New York City","bar":"Keith McNally''s establishments","country":"USA"},"creator":{"name":"Dale DeGroff (popularized)","profession":"bartender"},"story":{"en":"The French Martini emerged in the 1980s at Keith McNally''s New York establishments and was popularized by legendary bartender Dale DeGroff in the 1990s. The name \"French\" refers to Chambord, a French raspberry liqueur from the Loire Valley. The drink became a symbol of 1990s cocktail culture and the vodka craze of that era. Its distinctive frothy texture comes from the pineapple juice when shaken vigorously. The cocktail experienced a resurgence in the 2010s-2020s as part of the nostalgic revival of ''90s drinks."},"named_after":{"en":"Named \"French\" for Chambord, the French raspberry liqueur, and \"Martini\" for its elegant, sophisticated presentation in a cocktail glass."}}'::jsonb, '{"profile":["fruity","sweet","smooth","elegant"],"description":{"en":"Luxuriously fruity with sweet-tart raspberry from Chambord, tropical pineapple sweetness, and smooth vodka character. The vigorous shaking creates a silky foam texture that adds elegance. The combination is indulgent yet balanced, with the raspberry and pineapple flavors complementing each other beautifully."},"first_impression":{"en":"Sweet raspberry and tropical pineapple with elegant foam texture"},"finish":{"en":"Smooth, fruity finish with lingering raspberry sweetness"},"balance":{"en":"Well balanced between raspberry liqueur sweetness and pineapple tartness with smooth vodka base"}}'::jsonb, '{"best_time":["evening","late_night","celebration"],"occasions":["date_night","celebration","cocktail_party","glamorous_evening"],"seasons":["spring","summer","autumn","winter"],"food_pairings":{"en":"Pairs well with chocolate desserts, cheese plates, fresh berries, creamy dishes, and light appetizers."},"ideal_for":{"en":"Perfect for those who enjoy fruity, elegant cocktails with a luxurious feel. Ideal for celebrations, date nights, and anyone seeking a glamorous ''90s throwback. Great for vodka enthusiasts who appreciate fruit-forward drinks."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_CHAMBORD","quantity":{"amount":15,"unit":"ml"},"display_name":"Chambord (raspberry liqueur)","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":45,"unit":"ml"},"display_name":"Pineapple juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 15-20 seconds to create a frothy texture. Fine strain into a chilled Martini glass (coupe also works well). The drink should have a beautiful foam cap. Garnish with a lemon twist or fresh raspberry."}'::jsonb, 'Martini glass (or Coupe)', '{"en":"Lemon twist or fresh raspberry"}'::jsonb, 'none', 'up', '{"ING_VODKA"}', '{"fruity","sweet","smooth","elegant"}', 15, 190, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn","winter"}', '{"date_night","celebration","cocktail_party"}', false, false, '{"pornstar-martini","raspberry-martini"}', 'Chambord is essential - do not substitute with other raspberry liqueurs. Vigorous shaking creates signature foam. Fresh pineapple juice preferred. Fine strain for smooth texture.', 'mid', 85, 'https://iba-world.com/french-martini/', 'IBA Official Recipe. Popularized by Dale DeGroff in 1990s New York.', 1)
