INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e5f6a7b8-9c0d-1e2f-3a4b-5c6d7e8f9a0b', 'jungle-bird', 'c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7', '{"en":"Jungle Bird"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","tiki","tropical","rum","bitter","modern-classic"}', '{"en":"A bold tropical cocktail that breaks the tiki mold by incorporating bitter Campari with rum and pineapple. This Malaysian creation balances sweet tropical fruit with sophisticated bitterness, creating a complex and refreshing drink that has become a modern tiki favorite."}'::jsonb, '{"created_year":"1978","origin":{"city":"Kuala Lumpur","bar":"Kuala Lumpur Hilton","country":"Malaysia"},"creator":{"name":"Jeffrey Ong","profession":"bartender"},"story":{"en":"Created in 1978 by Jeffrey Ong at the Kuala Lumpur Hilton''s Aviary Bar for the hotel''s opening. The drink was named after the hornbills that inhabited the bar''s aviary. While initially a hotel signature, it remained relatively obscure until Giuseppe Gonzalez reintroduced it to New York''s cocktail scene in the 2000s. The unusual combination of Campari in a tiki drink was revolutionary and helped spark renewed interest in tiki cocktails."},"named_after":{"en":"Named after the hornbills (jungle birds) that were kept in an aviary at the Kuala Lumpur Hilton''s bar where the drink was created."}}'::jsonb, '{"profile":["bitter","tropical","complex","balanced"],"description":{"en":"Beautifully complex with bittersweet Campari playing against tropical pineapple sweetness, dark rum richness, and bright lime acidity. The bitterness cuts through the tropical elements creating a sophisticated, layered experience."},"first_impression":{"en":"Bitter Campari and tropical pineapple create an intriguing contrast"},"finish":{"en":"Long, bittersweet finish with rum warmth and lingering tropical notes"},"balance":{"en":"Masterfully balanced between bitter, sweet, sour, and boozy elements"}}'::jsonb, '{"best_time":["afternoon","evening","aperitif"],"occasions":["aperitivo","cocktail_party","tropical_gathering","date_night"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with spicy Asian cuisine, grilled meats, jerk chicken, Malaysian satay, and bold tropical flavors."},"ideal_for":{"en":"Perfect for adventurous drinkers who appreciate complex, bitter flavors in their tiki drinks. Ideal for Campari lovers and those seeking a sophisticated tropical cocktail. Great as an aperitif or with spicy food."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":45,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a rocks glass or tiki mug filled with fresh ice (preferably crushed). Garnish with a pineapple wedge and a maraschino cherry."}'::jsonb, 'Rocks glass or Tiki mug', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK"}', '{"bitter","tropical","complex","balanced"}', 16, 200, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","party","date_night"}', false, false, '{"mezcal-jungle-bird","jungle-bird-swizzle"}', 'Campari is essential - do not substitute. Use a dark Jamaican rum for best results. Fresh pineapple juice preferred. The bitterness balances the tropical sweetness - do not reduce Campari.', 'mid', 80, 'https://iba-world.com/jungle-bird/', 'IBA Official Recipe. Created by Jeffrey Ong at Kuala Lumpur Hilton, 1978.', 1)
