VALUES ('8b9c0d1e-2f3a-4b5c-6d7e-8f9a0b1c2d3e', 'lemon-drop', 'b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7', '{"en":"Lemon Drop"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","vodka","citrus","sweet","modern"}', '{"en":"A sweet and tart vodka cocktail inspired by the classic lemon drop candy. This modern classic combines citrus vodka with fresh lemon juice and simple syrup, served with a sugared rim."}'::jsonb, '{"created_year":"1970","origin":{"city":"San Francisco","bar":"Henry Africa''s","country":"USA"},"creator":{"name":"Norman Jay Hobday","profession":"bartender"},"story":{"en":"The Lemon Drop was created in the 1970s by Norman Jay Hobday at Henry Africa''s bar in San Francisco. Inspired by the popular lemon drop candy, Hobday created a cocktail version that captured the same sweet-tart flavor profile. The drink became hugely popular in the 1980s and 1990s, particularly among vodka cocktail enthusiasts. Its bright, accessible flavor made it a staple of modern cocktail culture."},"named_after":{"en":"Named after the popular lemon drop candy, known for its sweet-tart flavor."}}'::jsonb, '{"profile":["citrus","sweet","tart"],"description":{"en":"Bright, zesty lemon flavor with vodka smoothness and balanced sweetness. The sugared rim adds extra sweetness and textural contrast. Refreshing and candy-like."},"first_impression":{"en":"Bright lemon tartness with sugar sweetness"},"finish":{"en":"Clean, crisp finish with lingering citrus notes"},"balance":{"en":"Well-balanced between lemon tartness and sugar sweetness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["party","celebration","girls_night","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light appetizers, seafood, salads, or lemon desserts. Perfect as a palate cleanser."},"ideal_for":{"en":"Perfect for those who enjoy sweet, citrusy cocktails. Ideal for vodka lovers or anyone seeking a refreshing, candy-inspired drink."}}'::jsonb, '[{"ingredient_id":"ING_VODKA_CITRUS","quantity":{"amount":25,"unit":"ml"},"display_name":"Citrus vodka","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":20,"unit":"ml"},"display_name":"Triple sec","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Rim a chilled cocktail glass with sugar. Shake all ingredients with ice. Strain into the prepared glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Sugar rim and lemon twist"}'::jsonb, 'none', 'up', '{"ING_VODKA_CITRUS"}', '{"citrus","sweet","tart"}', 20, 160, 'easy', 60, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"party","celebration","brunch"}', false, false, '{"lemon-drop-shot"}', 'Sugar rim is essential for authentic presentation. Can use regular vodka if citrus vodka unavailable. Shake vigorously for proper dilution.', 'mid', 85, 'https://iba-world.com/lemon-drop/', 'IBA Official Recipe', 1)
