-- 91. Missionary's Downfall
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b2c3d4e5-6f7a-8b9c-0d1e-2f3a4b5c6d7e', 'missionarys-downfall', 'f5a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4', '{"en":"Missionary''s Downfall"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","tiki","tropical","rum","frozen","fruity"}', '{"en":"A vibrant frozen tiki cocktail featuring fresh mint, pineapple, and white rum. This refreshing drink combines the coolness of mint with tropical pineapple and peach flavors, creating an irresistible frozen treat that lives up to its provocative name."}'::jsonb, '{"created_year":"1950","origin":{"city":"Hollywood","bar":"Don the Beachcomber''s","country":"USA"},"creator":{"name":"Donn Beach (Ernest Raymond Beaumont Gantt)","profession":"bartender"},"story":{"en":"Created by Donn Beach (Don the Beachcomber) around 1950, this playfully named cocktail became a tiki bar favorite. The name humorously suggests that even the most virtuous missionary could be tempted by this delicious frozen concoction. The drink''s combination of fresh mint and tropical fruit was innovative for its time and remains a beloved frozen tiki classic."},"named_after":{"en":"The humorous name suggests the drink is so tempting it could lead a missionary astray from their virtuous path."}}'::jsonb, '{"profile":["tropical","refreshing","minty","fruity"],"description":{"en":"Brilliantly refreshing with a perfect balance of cooling mint, sweet tropical pineapple, subtle peach notes, and clean rum character. The frozen texture makes it incredibly smooth and easy to drink."},"first_impression":{"en":"Fresh mint and tropical pineapple hit first with icy coolness"},"finish":{"en":"Clean, refreshing finish with lingering mint and fruit sweetness"},"balance":{"en":"Perfectly balanced between mint freshness, tropical fruit sweetness, and rum backbone"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","summer_gathering","tiki_party"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with grilled fish, tropical fruit salads, coconut shrimp, fish tacos, and light Asian cuisine."},"ideal_for":{"en":"Perfect for hot summer days and beach settings. Ideal for those who love frozen tropical drinks with a refreshing mint twist. Great for tiki enthusiasts and anyone seeking a cooling, fruity rum cocktail."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":60,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_PEACH_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Peach liqueur","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_PINEAPPLE_FRESH","quantity":{"amount":60,"unit":"g"},"display_name":"Fresh pineapple chunks","notes":null},{"ingredient_id":"ING_MINT_LEAVES","quantity":{"amount":10,"unit":"leaves"},"display_name":"Fresh mint leaves","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'blend', '{"en":"Add all ingredients to a blender with one cup of crushed ice. Blend until smooth and slushy. Pour into a chilled tiki mug or hurricane glass. Garnish with a pineapple wedge, mint sprig, and optionally a cherry."}'::jsonb, 'Tiki mug or Hurricane glass', '{"en":"Pineapple wedge, fresh mint sprig, maraschino cherry"}'::jsonb, 'blended', 'frozen', '{"ING_RUM_WHITE"}', '{"tropical","refreshing","minty","fruity"}', 12, 220, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","party","casual"}', false, false, '{"virgin-missionarys-downfall"}', 'Fresh pineapple is essential - do not use canned. Blend thoroughly for smooth texture. Can adjust ice quantity for desired consistency. Mint should be fresh and aromatic.', 'mid', 75, 'https://iba-world.com/missionarys-downfall/', 'IBA Official Recipe. Historical information from Don the Beachcomber archives.', 1)
