-- 92. Naked and Famous
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a8f3c5d1-4b2e-4d9a-9c7f-1e8a6b3d5f2c', 'naked-and-famous', 'e4f6a8c2d1b9e7f5a3c8d6b4e2f1a9c7', '{"en":"Naked and Famous"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","smoky","bitter","complex"}', '{"en":"A modern classic featuring mezcal, Aperol, yellow Chartreuse, and lime. The Naked and Famous is a perfectly balanced, smoky, bitter, and herbal cocktail that showcases the complexity of equal-part mixing."}'::jsonb, '{"created_year":"2011","origin":{"city":"New York City","bar":"Death & Co","country":"USA"},"creator":{"name":"Joaquín Simó","profession":"bartender"},"story":{"en":"Created in 2011 by Joaquín Simó at Death & Co in New York City, the Naked and Famous is a variation on the classic Last Word cocktail. Simó replaced the gin with mezcal, the green Chartreuse with yellow Chartreuse, and the maraschino with Aperol, creating a completely new flavor profile while maintaining the equal-parts structure. The drink quickly became a modern classic and helped popularize mezcal in craft cocktails."},"named_after":{"en":"Named after the song \"Naked and Famous\" by The Presidents of the United States of America, a band that Joaquín Simó enjoyed."}}'::jsonb, '{"profile":["smoky","bitter","herbal","citrus"],"description":{"en":"Complex and sophisticated. The smoky mezcal provides an earthy backbone, while Aperol adds bittersweet orange notes. Yellow Chartreuse contributes honey and herbal complexity, and fresh lime juice brings brightness and acidity to tie everything together."},"first_impression":{"en":"Smoky mezcal and bright citrus hit first, followed by the herbal complexity of Chartreuse"},"finish":{"en":"Long, smoky finish with lingering herbal and bitter orange notes"},"balance":{"en":"Perfectly balanced equal-parts cocktail - each ingredient shines while contributing to a harmonious whole"}}'::jsonb, '{"best_time":["evening","late_afternoon"],"occasions":["aperitivo","date_night","sophisticated_gathering","craft_cocktail_experience"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with grilled meats, Mexican cuisine, tapas, aged cheeses, and smoked fish. The smoky mezcal pairs beautifully with charred or smoky foods."},"ideal_for":{"en":"Perfect for adventurous drinkers who appreciate complex, spirit-forward cocktails. A must-try for mezcal lovers and those exploring modern classics. Ideal for sophisticated palates seeking something beyond the ordinary."}}'::jsonb, '[{"ingredient_id":"ING_MEZCAL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Mezcal","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Aperol","notes":null},{"ingredient_id":"ING_CHARTREUSE_YELLOW","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Yellow Chartreuse","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake well until properly chilled. Strain into a chilled coupe glass."}'::jsonb, 'Coupe glass', '{"en":"None (or lime wheel)"}'::jsonb, 'none', 'up', '{"ING_MEZCAL"}', '{"smoky","bitter","herbal","citrus"}', 25, 180, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","sophisticated_gathering"}', false, false, '{"last-word","final-ward","paper-plane"}', 'Use high-quality mezcal with good smoke character. Fresh lime juice is essential. Equal parts (22.5ml each) is critical to balance. Shake hard to properly integrate the Chartreuse. This is a variation of the Last Word - educate guests on the connection.', 'premium', 75, 'https://iba-world.com/naked-and-famous/', 'IBA Official Recipe. Created by Joaquín Simó at Death & Co, New York City, 2011.', 1)
