INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d0e1f2a3-4b5c-6d7e-8f9a-0b1c2d3e4f5a', 'paper-plane', 'b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2', '{"en":"Paper Plane"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","whiskey","amaro","balanced","contemporary"}', '{"en":"A modern classic featuring bourbon, Aperol, Amaro Nonino, and lemon juice in equal parts. This perfectly balanced cocktail combines bitter, sweet, and sour elements with whiskey warmth, creating an incredibly drinkable and sophisticated drink that has become a contemporary favorite."}'::jsonb, '{"created_year":"2008","origin":{"city":"Chicago","bar":"The Violet Hour","country":"USA"},"creator":{"name":"Sam Ross","profession":"bartender"},"story":{"en":"Created by Sam Ross at The Violet Hour in Chicago in 2008, inspired by the M.I.A. song \"Paper Planes.\" Ross wanted to create a Last Word variation using bourbon instead of gin. The equal-parts formula (¾ oz of each ingredient) creates perfect harmony between bourbon''s sweetness, Aperol''s bitter orange, Amaro Nonino''s complex herbal notes, and lemon''s acidity. The drink spread rapidly through cocktail bars worldwide and became one of the defining cocktails of the craft cocktail renaissance."},"named_after":{"en":"Named after the M.I.A. song \"Paper Planes\" which was playing when Sam Ross created the drink."}}'::jsonb, '{"profile":["balanced","bitter","citrus","complex"],"description":{"en":"Perfectly harmonious with bourbon''s caramel and vanilla notes balanced by Aperol''s bitter orange, Amaro Nonino''s herbal complexity, and bright lemon acidity. No single element dominates, creating a beautifully integrated whole that''s both sophisticated and approachable."},"first_impression":{"en":"Balanced bitter-sweet with bourbon warmth and citrus brightness"},"finish":{"en":"Clean, lingering finish with herbal bitterness and whiskey warmth"},"balance":{"en":"Exemplary balance - the equal parts formula creates perfect harmony between all elements"}}'::jsonb, '{"best_time":["evening","aperitif","after_dinner"],"occasions":["aperitivo","cocktail_party","date_night","casual_elegance"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with charcuterie, aged cheeses, grilled meats, Italian antipasti, and roasted vegetables."},"ideal_for":{"en":"Perfect for bourbon and amaro lovers seeking a balanced modern classic. Ideal for those who appreciate bitter flavors in harmony with whiskey. Great as an aperitif or digestif."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Bourbon","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Aperol","notes":null},{"ingredient_id":"ING_AMARO_NONINO","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Amaro Nonino","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled (about 15 seconds). Double strain into a chilled coupe or Nick & Nora glass. No garnish needed - the drink speaks for itself."}'::jsonb, 'Coupe or Nick & Nora glass', '{"en":"None (optional: lemon twist)"}'::jsonb, 'none', 'up', '{"ING_BOURBON"}', '{"balanced","bitter","citrus","complex"}', 21, 160, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","cocktail_party"}', false, false, '{"rye-paper-plane","mezcal-paper-plane"}', 'Equal parts (¾ oz each) is essential for balance. Amaro Nonino is critical - do not substitute. Fresh lemon juice required. Double strain for clarity. Some prefer rye whiskey for extra spice.', 'mid', 90, 'https://iba-world.com/paper-plane/', 'IBA Official Recipe. Created by Sam Ross at The Violet Hour, Chicago, 2008.', 1)
