-- 112. Ve.N.To (Vento)
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f2a3b4c5-6d7e-8f9a-0b1c-2d3e4f5a6b7c', 'vento', 'd5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4', '{"en":"Ve.N.To (Vento)"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","italian","gin","vermouth","aperitivo","refreshing"}', '{"en":"A light and refreshing Italian aperitivo cocktail combining gin, sweet vermouth, and tonic water. The name Ve.N.To is an acronym for the ingredients: Vermouth, Neutral spirit (gin), and Tonic. This elegant serve celebrates the Venetian aperitivo tradition with simplicity and style."}'::jsonb, '{"created_year":"2017","origin":{"city":"Venice","bar":"Unknown","country":"Italy"},"creator":{"name":"Various bartenders in Venice","profession":"bartender"},"story":{"en":"The Ve.N.To emerged from Venice''s aperitivo culture in the 2010s as a lighter alternative to the Negroni and Americano. Created by Venetian bartenders, the acronym Ve.N.To cleverly represents its three components: Vermouth, Neutral spirit, and Tonic. The drink became popular across Venice''s bars and eventually spread throughout Italy and internationally. It embodies the modern Italian approach to aperitivo: refreshing, lower in alcohol, and perfect for social drinking before dinner."},"named_after":{"en":"Ve.N.To is an acronym: Ve (Vermouth) + N (Neutral spirit/gin) + To (Tonic). The name also means \"wind\" in Italian, suggesting its light, refreshing nature."}}'::jsonb, '{"profile":["refreshing","herbal","light","effervescent"],"description":{"en":"Light and refreshing with gin''s botanical character balanced by sweet vermouth''s herbal complexity and tonic water''s effervescence and subtle bitterness. The drink is wonderfully sessionable and perfectly captures the spirit of Italian aperitivo hour."},"first_impression":{"en":"Light botanical gin with sweet herbal vermouth and refreshing bubbles"},"finish":{"en":"Clean, refreshing finish with lingering herbal notes and subtle bitterness"},"balance":{"en":"Perfectly balanced between botanical complexity, sweet herbal notes, and refreshing effervescence"}}'::jsonb, '{"best_time":["afternoon","early_evening","aperitif"],"occasions":["aperitivo","social_gathering","casual","pre_dinner"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Perfect with Italian cicchetti, olives, salted nuts, cheese and charcuterie, bruschetta, and light antipasti."},"ideal_for":{"en":"Perfect for aperitivo hour and those seeking a lighter, refreshing alternative to heavier cocktails. Ideal for extended social drinking sessions and warm weather. Great for introducing people to aperitivo culture."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":30,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_TONIC_WATER","quantity":{"amount":90,"unit":"ml"},"display_name":"Tonic water","notes":null}]'::jsonb, 'build', '{"en":"Fill a large wine glass or highball glass with ice cubes. Add gin and sweet vermouth. Stir gently to combine. Top with tonic water. Stir once briefly to integrate. Garnish with an orange slice or twist."}'::jsonb, 'Wine glass or Highball glass', '{"en":"Orange slice or twist"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_GIN"}', '{"refreshing","herbal","light","effervescent"}', 8, 140, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free","low-abv"}', '{"spring","summer","autumn"}', '{"aperitivo","casual","social"}', false, false, '{"vento-bianco","vento-rosso"}', 'Use quality sweet vermouth (Carpano Antica, Cocchi). Good quality tonic water is important. Stir gently to preserve carbonation. Can use larger wine glass for more elegant presentation.', 'mid', 70, 'https://iba-world.com/vento/', 'IBA Official Recipe. Originated from Venice''s aperitivo culture, 2017.', 1)
