-- 118. Bahama Mama
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f8a2c1d4-7b3e-4f9a-8c2d-1e5f6a7b8c9d', 'bahama-mama', 'bahama-mama-tiki-tropical-famous-2025', '{"en":"Bahama Mama"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","fruity","party"}', '{"en":"A vibrant tropical cocktail combining rum, coconut liqueur, and tropical fruit juices. The Bahama Mama is a vacation in a glass, delivering sweet, fruity flavors with a strong rum backbone that transports you straight to the Caribbean beaches."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Nassau","bar":"Bahamas Beach Bars","country":"Bahamas"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Bahama Mama emerged in the 1960s during the golden age of tiki culture, created in the beach bars of Nassau, Bahamas. Designed to appeal to tourists seeking tropical paradise in a glass, this cocktail became synonymous with Caribbean vacation culture. The drink combines multiple rums with tropical juices and coconut, creating a potent yet dangerously drinkable concoction that embodies the carefree island lifestyle."},"named_after":{"en":"Named after the Bahamas islands and the warm, welcoming nature of Caribbean mothers (mama), representing the nurturing, tropical hospitality of the region."}}'::jsonb, '{"profile":["fruity","sweet","tropical"],"description":{"en":"Sweet and tropical with layers of pineapple, coconut, and orange flavors. The dark rum provides depth while coffee liqueur adds subtle complexity. Refreshing and dangerously smooth."},"first_impression":{"en":"Bright pineapple and orange burst with coconut creaminess"},"finish":{"en":"Long, warming rum finish with lingering coconut and coffee notes"},"balance":{"en":"Well-balanced sweetness with strong rum presence - tropical flavors dominate but the alcohol is clearly present"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","celebration","vacation","summer_gathering"],"seasons":["summer","spring"],"food_pairings":{"en":"Perfect with Caribbean jerk chicken, fish tacos, grilled shrimp, tropical fruit platters, or coconut shrimp. Also pairs well with spicy foods as the sweetness balances heat."},"ideal_for":{"en":"Perfect for beach parties, poolside relaxation, and tropical-themed celebrations. Ideal for those who love sweet, fruity cocktails with a strong kick. A crowd-pleaser at summer gatherings."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_RUM_COCONUT","quantity":{"amount":30,"unit":"ml"},"display_name":"Coconut rum","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Coffee liqueur","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Orange juice","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a hurricane glass filled with fresh ice. Garnish with pineapple wedge and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_DARK","ING_RUM_COCONUT"}', '{"fruity","sweet","tropical"}', 15, 320, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","spring"}', '{"beach","pool_party","celebration","vacation"}', false, false, '{"virgin-bahama-mama","bahama-mama-frozen"}', 'Use fresh pineapple juice for best flavor. Can be blended with ice for frozen version. Some recipes include grenadine for color - add 15ml if desired. Very popular poolside drink.', 'mid', 75, 'https://www.liquor.com/recipes/bahama-mama/', 'Classic tiki cocktail recipe. Variations exist across Caribbean bars.', 1)
