-- 120. Bay Breeze
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('2b4f8e1c-9d3a-4e7b-a1c5-6f8d2e3a9b7c', 'bay-breeze', 'bay-breeze-tiki-tropical-famous-2025', '{"en":"Bay Breeze"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","vodka","fruity","refreshing","easy"}', '{"en":"A light and refreshing tropical cocktail combining vodka with cranberry and pineapple juices. The Bay Breeze is the perfect poolside sipper, offering a beautiful pink hue and balanced sweet-tart flavor that''s dangerously easy to drink."}'::jsonb, '{"created_year":"1980s","origin":{"city":"Miami","bar":"Beach Bars","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Bay Breeze emerged in the 1980s as a variation of the classic Sea Breeze, swapping grapefruit juice for pineapple. Created in Miami beach bars during the height of vodka cocktail popularity, it became a staple of tropical resort menus. The drink''s name evokes the gentle ocean breezes of coastal bays, while its easy-drinking nature made it a favorite among beachgoers and pool parties."},"named_after":{"en":"Named after the refreshing ocean breezes that blow across coastal bays, evoking a sense of relaxation and tropical vacation."}}'::jsonb, '{"profile":["fruity","sweet","tart"],"description":{"en":"Bright and refreshing with a perfect balance of tart cranberry and sweet pineapple. The vodka provides a clean, neutral base that lets the fruit juices shine. Light, crisp, and incredibly refreshing."},"first_impression":{"en":"Tart cranberry hits first, followed by tropical pineapple sweetness"},"finish":{"en":"Clean, crisp finish with lingering fruit notes and subtle vodka warmth"},"balance":{"en":"Perfectly balanced between sweet and tart - the pineapple and cranberry complement each other beautifully"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","brunch","casual_gathering","summer_day"],"seasons":["summer","spring"],"food_pairings":{"en":"Pairs beautifully with seafood appetizers, ceviche, grilled fish, tropical salads, or light summer fare. Also excellent with brunch dishes like eggs benedict or smoked salmon."},"ideal_for":{"en":"Perfect for beach days, poolside lounging, and casual summer gatherings. Ideal for those who prefer lighter, refreshing cocktails over heavy tiki drinks. A great choice for brunch or afternoon sipping."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Cranberry juice","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Add vodka, then cranberry juice, and top with pineapple juice. Stir gently to combine. Garnish with lime wedge and fresh pineapple."}'::jsonb, 'Highball glass', '{"en":"Lime wedge and pineapple slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"fruity","sweet","tart"}', 8, 180, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","spring"}', '{"beach","pool_party","brunch","casual_gathering"}', false, false, '{"sea-breeze","madras","virgin-bay-breeze"}', 'Very easy to make - just build in glass. Use quality cranberry juice (not cocktail). Can adjust juice ratios to taste. Popular brunch and poolside drink. Sister cocktails: Sea Breeze (grapefruit instead of pineapple) and Madras (orange instead of pineapple).', 'budget', 70, 'https://www.liquor.com/recipes/bay-breeze/', 'Classic tropical cocktail, variation of Sea Breeze.', 1)
