-- 121. Bee's Kiss
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b8a4c9f3-5d2e-4b1f-9e6a-2d3f4e5a6b7c', 'bees-kiss', 'e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4', '{"en":"Bee''s Kiss"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","rum","sweet","creamy"}', '{"en":"A luscious Prohibition-era cocktail featuring rum, honey, and heavy cream. The Bee''s Kiss is a forgotten classic that delivers smooth sweetness with a velvety texture and tropical warmth."}'::jsonb, '{"created_year":"1930","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Bee''s Kiss appeared in the early 1930s as Prohibition was ending. Featured in Frank Meier''s \"The Artistry of Mixing Drinks\" (1936), this cocktail was part of the \"Bee''s Knees\" family of drinks that used honey to sweeten and mask lower-quality spirits. The creamy version became popular in Cuba and eventually made its way to American cocktail bars."},"named_after":{"en":"Named after the sweetness of a bee''s kiss, referencing the honey used in the recipe and the smooth, sweet character of the drink."}}'::jsonb, '{"profile":["sweet","creamy","smooth"],"description":{"en":"Rich and velvety with honey sweetness balanced by the warmth of rum and smoothness of cream. A decadent, dessert-like cocktail with tropical undertones."},"first_impression":{"en":"Creamy honey sweetness upfront with gentle rum warmth"},"finish":{"en":"Smooth, lingering honey finish with subtle rum spice"},"balance":{"en":"Well-balanced between sweetness and spirit - indulgent but not cloying"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["date_night","dessert","nightcap","special_occasion"],"seasons":["autumn","winter"],"food_pairings":{"en":"Excellent with chocolate desserts, banana foster, crème brûlée, or as a dessert replacement. Pairs well with tropical fruits and caramelized sweets."},"ideal_for":{"en":"Perfect for rum lovers who enjoy creamy, dessert-style cocktails. Ideal for those seeking a sweet, indulgent drink with vintage charm."}}'::jsonb, '[{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":45,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Honey syrup","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake vigorously until well-chilled and properly emulsified. Strain into a chilled coupe glass. No garnish needed."}'::jsonb, 'Coupe glass', '{"en":"None"}'::jsonb, 'none', 'up', '{"ING_WHITE_RUM"}', '{"sweet","creamy","smooth"}', 15, 240, 'easy', 60, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter"}', '{"date_night","dessert","nightcap","special_occasion"}', false, false, '{"bees-knees","gold-rush"}', 'Shake hard to properly emulsify cream with honey. Use honey syrup (3:1 honey to water) for easier mixing. Can substitute aged rum for deeper flavor. Ensure all ingredients are well-chilled before shaking.', 'mid', 55, 'https://www.diffordsguide.com/cocktails/recipe/384/bees-kiss', 'Based on Frank Meier''s 1936 recipe. Historical information from vintage cocktail books.', 1)
