-- 125. Blue Lagoon
INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('5e9a3b2f-7c1d-4a8e-9f3b-2d6c8a1e4f7b', 'blue-lagoon', 'blue-lagoon-tiki-tropical-famous-2025', '{"en":"Blue Lagoon"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","vodka","blue-curacao","colorful","instagram"}', '{"en":"A visually stunning cocktail featuring vodka, blue curaçao, and lemonade, creating an electric blue color reminiscent of tropical lagoons. The Blue Lagoon is as beautiful as it is refreshing, delivering citrus sweetness with a vibrant presentation."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Paris","bar":"Harry''s New York Bar","country":"France"},"creator":{"name":"Andy MacElhone","profession":"bartender"},"story":{"en":"The Blue Lagoon was created in the 1960s by Andy MacElhone at Harry''s New York Bar in Paris, the same legendary establishment that gave us the Bloody Mary and French 75. The cocktail was designed to showcase blue curaçao, a liqueur that was gaining popularity at the time. Its striking electric blue color made it an instant hit, especially as color photography became more prevalent in advertising. The drink gained even more fame when it shared its name with the 1980 film \"The Blue Lagoon,\" starring Brooke Shields."},"named_after":{"en":"Named for its stunning electric blue color that evokes crystal-clear tropical lagoons and azure waters of paradise islands."}}'::jsonb, '{"profile":["citrus","sweet","refreshing"],"description":{"en":"Sweet and citrusy with orange notes from the blue curaçao and tart lemon from the lemonade. The vodka provides a clean, neutral base. Light, refreshing, and crowd-pleasing with a beautiful tropical character."},"first_impression":{"en":"Bright lemon sweetness with orange citrus notes from curaçao"},"finish":{"en":"Clean, crisp finish with lingering citrus and subtle vodka warmth"},"balance":{"en":"Well-balanced sweetness with bright citrus acidity - refreshing without being cloying"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["pool_party","beach","celebration","instagram","summer_gathering"],"seasons":["summer","spring"],"food_pairings":{"en":"Pairs well with light appetizers, seafood, grilled fish, tropical fruit platters, or coconut shrimp. Also excellent with Asian fusion cuisine and sushi."},"ideal_for":{"en":"Perfect for pool parties, beach gatherings, and tropical-themed events. Ideal for those who love visually striking cocktails and sweet, easy-drinking beverages. A guaranteed Instagram hit and conversation starter."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_BLUE_CURACAO","quantity":{"amount":30,"unit":"ml"},"display_name":"Blue curaçao","notes":null},{"ingredient_id":"ING_LEMONADE","quantity":{"amount":90,"unit":"ml"},"display_name":"Lemonade","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Add vodka and blue curaçao. Top with lemonade and stir gently. Garnish with lemon wheel and maraschino cherry."}'::jsonb, 'Highball glass', '{"en":"Lemon wheel and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"citrus","sweet","refreshing"}', 10, 200, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","spring"}', '{"pool_party","beach","celebration","instagram"}', false, false, '{"electric-lemonade","blue-hawaiian","swimming-pool"}', 'Very easy to make and visually stunning. The electric blue color is the main attraction. Can substitute Sprite for lemonade if unavailable. Popular for Instagram photos - ensure good presentation. Some recipes use lemon-lime soda instead of lemonade.', 'budget', 80, 'https://www.liquor.com/recipes/blue-lagoon/', 'Classic cocktail from Harry''s New York Bar, Paris. Recipe variations exist.', 1)
